            <>
              <span>📄</span>
              <span>上传PDF论文</span>
            </>
          )}
        </Button>

        {uploadStatus && (
          <span className={`text-sm ${
            uploadStatus.includes('成功') ? 'text-green-600' : 
            uploadStatus.includes('失败') ? 'text-red-600' : 'text-blue-600'
          }`}>
            {uploadStatus}
          </span>
        )}
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept=".pdf"
        onChange={handleFileSelect}
        className="hidden"
      />

