// CLI统计相关类型定义
export interface CLIWeeklyStats {
  user_id: string;
  username: string;
  email: string;
  week_start: string;
  week_end: string;
  total_messages: number;
  total_sessions: number;
}

export interface CLIStatsResponse {
  week_start: string;
  week_end: string;
  stats: CLIWeeklyStats[];
  total_qualified_users: number;
}

export async function getCLIWeeklyStats(weeks: number = 4) {
  return backendRequest<CLIStatsResponse[]>(
    `/auth/admin/cli-stats?weeks=${weeks}`,
    'GET'
  );
}

