            for (let i = 0; i < BOARD_SIZE; i++) {
                for (let j = 0; j < BOARD_SIZE; j++) {
                    if (board[i][j] === 0) {
                        board[i][j] = 2;
                        if (checkWin(i, j)) {
                            board[i][j] = 0;
                            return { row: i, col: j };
                        }
                        board[i][j] = 0;
                    }
                }
