            // æ£€æŸ¥æ˜¯å¦éœ€è¦é˜²å®ˆ
            for (let i = 0; i < BOARD_SIZE; i++) {
                for (let j = 0; j < BOARD_SIZE; j++) {
                    if (board[i][j] === 0) {
                        board[i][j] = 1;
                        if (checkWin(i, j)) {
