            </div>
        </div>
    </div>

    <div class="winner-overlay" id="winner-overlay">
        <div class="winner-message">
            <h2 id="winner-text">é»‘æ–¹è·èƒœï¼</h2>
            <div class="winner-piece" id="winner-piece"></div>
            <button class="restart-btn" onclick="restartGame()">å†æ¥ä¸€å±€</button>
        </div>
    </div>

    <script>
        const BOARD_SIZE = 15;
        let board = Array(BOARD_SIZE).fill(null).map(() => Array(BOARD_SIZE).fill(0));
        let currentPlayer = 1; // 1 = é»‘, 2 = ç™½
        let gameOver = false;
        let moveHistory = [];
        let gameMode = 'pvp'; // 'pvp' or 'pve'

        // æ˜Ÿä½åæ ‡ (3,3), (3,11), (7,7), (11,3), (11,11), (3,7), (7,3), (7,11), (11,7)
        const starPoints = [
            [3, 3], [3, 7], [3, 11],
            [7, 3], [7, 7], [7, 11],
            [11, 3], [11, 7], [11, 11]
        ];

        function initBoard() {
            const boardElement = document.getElementById('board');
            boardElement.innerHTML = '';

            // åˆ›å»ºåæ ‡æ ‡ç­¾
            const coordTop = document.getElementById('coord-top');
            const coordBottom = document.getElementById('coord-bottom');
            const coordLeft = document.getElementById('coord-left');
            const coordRight = document.getElementById('coord-right');

            coordTop.innerHTML = '';
            coordBottom.innerHTML = '';
            coordLeft.innerHTML = '';
            coordRight.innerHTML = '';

            for (let i = 0; i < BOARD_SIZE; i++) {
                const label = String.fromCharCode(65 + i);
                coordTop.innerHTML += `<span>${label}</span>`;
                coordBottom.innerHTML += `<span>${label}</span>`;
                coordLeft.innerHTML += `<span>${i + 1}</span>`;
                coordRight.innerHTML += `<span>${i + 1}</span>`;
            }

            for (let i = 0; i < BOARD_SIZE; i++) {
                for (let j = 0; j < BOARD_SIZE; j++) {
                    const cell = document.createElement('div');
                    cell.className = 'cell';
                    cell.dataset.row = i;
                    cell.dataset.col = j;

                    // æ·»åŠ æ˜Ÿä½æ ‡è®°
                    if (starPoints.some(([r, c]) => r === i && c === j)) {
                        cell.classList.add('star-point');
                    }

                    cell.addEventListener('click', () => handleCellClick(i, j));
                    boardElement.appendChild(cell);
                }
            }
        }

        function handleCellClick(row, col) {
            if (gameOver || board[row][col] !== 0) return;

            // åœ¨PVEæ¨¡å¼ä¸‹ï¼Œå¦‚æœæ˜¯AIçš„å›åˆï¼Œä¸å…è®¸ç©å®¶ç‚¹å‡»
            if (gameMode === 'pve' && currentPlayer === 2) return;

            makeMove(row, col);
        }

        function makeMove(row, col) {
            if (board[row][col] !== 0) return;

            board[row][col] = currentPlayer;
            moveHistory.push({ row, col, player: currentPlayer });

            const cell = document.querySelector(`[data-row="${row}"][data-col="${col}"]`);
            const piece = document.createElement('div');
            piece.className = `piece ${currentPlayer === 1 ? 'black' : 'white'}`;
            cell.appendChild(piece);

            if (checkWin(row, col)) {
                gameOver = true;
                showWinner(currentPlayer);
                return;
            }

            currentPlayer = currentPlayer === 1 ? 2 : 1;
            updateStatus();
            updateUndoButton();

            // å¦‚æœæ˜¯PVEæ¨¡å¼ä¸”è½®åˆ°AI
            if (gameMode === 'pve' && currentPlayer === 2 && !gameOver) {
                setTimeout(aiMove, 500);
            }
        }

        function aiMove() {
            const move = findBestMove();
            if (move) {
                makeMove(move.row, move.col);
            }
        }

        function findBestMove() {
            // ç®€å•çš„AIç­–ç•¥ï¼š
            // 1. æ£€æŸ¥æ˜¯å¦èƒ½èµ¢
            // 2. æ£€æŸ¥æ˜¯å¦éœ€è¦é˜²å®ˆ
            // 3. é€‰æ‹©æœ€ä½³è¿›æ”»ä½ç½®

            // æ£€æŸ¥AIæ˜¯å¦èƒ½èµ¢
            for (let i = 0; i < BOARD_SIZE; i++) {
                for (let j = 0; j < BOARD_SIZE; j++) {
                    if (board[i][j] === 0) {
                        board[i][j] = 2;
                        if (checkWin(i, j)) {
                            board[i][j] = 0;
                            return { row: i, col: j };
                        }
                        board[i][j] = 0;
                    }
                }
            }

            // æ£€æŸ¥æ˜¯å¦éœ€è¦é˜²å®ˆ
            for (let i = 0; i < BOARD_SIZE; i++) {
                for (let j = 0; j < BOARD_SIZE; j++) {
                    if (board[i][j] === 0) {
                        board[i][j] = 1;
                        if (checkWin(i, j)) {
                            board[i][j] = 0;
                            return { row: i, col: j };
                        }
                        board[i][j] = 0;
                    }
                }
            }

            // è¯„ä¼°æ¯ä¸ªç©ºä½çš„å¾—åˆ†
            let bestScore = -1;
            let bestMove = null;

            for (let i = 0; i < BOARD_SIZE; i++) {
                for (let j = 0; j < BOARD_SIZE; j++) {
                    if (board[i][j] === 0) {
                        const score = evaluatePosition(i, j);
                        if (score > bestScore) {
                            bestScore = score;
                            bestMove = { row: i, col: j };
                        }
                    }
                }
            }

            return bestMove;
        }

        function evaluatePosition(row, col) {
            let score = 0;

            // ä¸­å¿ƒä½ç½®åŠ åˆ†
            const centerDistance = Math.abs(row - 7) + Math.abs(col - 7);
            score += (14 - centerDistance) * 2;

            // æ£€æŸ¥å‘¨å›´æ˜¯å¦æœ‰æ£‹å­
            const directions = [
                [0, 1], [1, 0], [1, 1], [1, -1]
            ];

            for (const [dx, dy] of directions) {
                let aiCount = 0;
                let playerCount = 0;
                let empty = 0;

                for (let step = -4; step <= 4; step++) {
                    const newRow = row + dx * step;
                    const newCol = col + dy * step;

                    if (newRow >= 0 && newRow < BOARD_SIZE && newCol >= 0 && newCol < BOARD_SIZE) {
                        if (board[newRow][newCol] === 2) aiCount++;
                        else if (board[newRow][newCol] === 1) playerCount++;
                        else empty++;
                    }
                }

                // æ ¹æ®è¿å­æ•°é‡è¯„åˆ†
                if (playerCount === 0) {
                    score += aiCount * aiCount * 10;
                }
                if (aiCount === 0) {
                    score += playerCount * playerCount * 8;
                }
            }

            return score;
        }

        function checkWin(row, col) {
            const player = board[row][col];
            const directions = [
                [[0, 1], [0, -1]],   // æ¨ª
                [[1, 0], [-1, 0]],   // ç«–
                [[1, 1], [-1, -1]],  // æ–œ
                [[1, -1], [-1, 1]]   // åæ–œ
            ];

            for (const [dir1, dir2] of directions) {
                let count = 1;
                let positions = [[row, col]];

                // æ­£æ–¹å‘
                for (let step = 1; step < 5; step++) {
                    const newRow = row + dir1[0] * step;
                    const newCol = col + dir1[1] * step;
                    if (newRow >= 0 && newRow < BOARD_SIZE && 
                        newCol >= 0 && newCol < BOARD_SIZE && 
                        board[newRow][newCol] === player) {
                        count++;
                        positions.push([newRow, newCol]);
                    } else {
                        break;
                    }
                }

                // åæ–¹å‘
                for (let step = 1; step < 5; step++) {
                    const newRow = row + dir2[0] * step;
                    const newCol = col + dir2[1] * step;
                    if (newRow >= 0 && newRow < BOARD_SIZE && 
                        newCol >= 0 && newCol < BOARD_SIZE && 
                        board[newRow][newCol] === player) {
                        count++;
                        positions.push([newRow, newCol]);
                    } else {
                        break;
                    }
                }

                if (count >= 5) {
                    highlightWinningLine(positions);
                    return true;
                }
            }

            return false;
        }

        function highlightWinningLine(positions) {
            // ç®€å•çš„èƒœåˆ©æç¤ºï¼Œå¯ä»¥æ·»åŠ è¿çº¿åŠ¨ç”»
            positions.forEach(([row, col]) => {
                const cell = document.querySelector(`[data-row="${row}"][data-col="${col}"]`);
                if (cell) {
                    cell.style.background = 'rgba(255, 215, 0, 0.3)';
                }
            });
        }

        function showWinner(player) {
            const overlay = document.getElementById('winner-overlay');
            const winnerText = document.getElementById('winner-text');
            const winnerPiece = document.getElementById('winner-piece');

            if (player === 1) {
                winnerText.textContent = gameMode === 'pve' ? 'ğŸ‰ æ­å–œä½ èµ¢äº†ï¼' : 'ğŸ‰ é»‘æ–¹è·èƒœï¼';
                winnerPiece.style.background = 'radial-gradient(circle at 30% 30%, #4a4a4a, #000)';
            } else {
                winnerText.textContent = gameMode === 'pve' ? 'ğŸ˜¢ AIè·èƒœï¼' : 'ğŸ‰ ç™½æ–¹è·èƒœï¼';
                winnerPiece.style.background = 'radial-gradient(circle at 30% 30%, #fff, #ddd)';
            }

            overlay.style.display = 'flex';
        }

        function updateStatus() {
            const statusText = document.getElementById('status-text');
            const currentPlayerIndicator = document.getElementById('current-player');

            if (gameMode === 'pvp') {
                statusText.textContent = currentPlayer === 1 ? 'é»‘æ–¹å›åˆ' : 'ç™½æ–¹å›åˆ';
            } else {
                statusText.textContent = currentPlayer === 1 ? 'ä½ çš„å›åˆï¼ˆé»‘ï¼‰' : 'AIæ€è€ƒä¸­...';
            }

            currentPlayerIndicator.style.background = currentPlayer === 1 ? 
                'radial-gradient(circle at 30% 30%, #4a4a4a, #000)' : 
                'radial-gradient(circle at 30% 30%, #fff, #ddd)';
        }

        function updateUndoButton() {
            const undoBtn = document.getElementById('undo-btn');
            undoBtn.disabled = moveHistory.length === 0;
        }

        function undoMove() {
            if (moveHistory.length === 0 || gameOver) return;

            // åœ¨PVEæ¨¡å¼ä¸‹ï¼Œä¸€æ¬¡æ‚”æ£‹è¦æ’¤é”€ä¸¤æ­¥ï¼ˆç©å®¶å’ŒAIï¼‰
            const steps = gameMode === 'pve' ? Math.min(2, moveHistory.length) : 1;

            for (let i = 0; i < steps; i++) {
                const lastMove = moveHistory.pop();
                board[lastMove.row][lastMove.col] = 0;

                const cell = document.querySelector(`[data-row="${lastMove.row}"][data-col="${lastMove.col}"]`);
                const piece = cell.querySelector('.piece');
                if (piece) {
                    piece.remove();
                }

                currentPlayer = lastMove.player;
            }

            updateStatus();
            updateUndoButton();
        }

        function restartGame() {
            board = Array(BOARD_SIZE).fill(null).map(() => Array(BOARD_SIZE).fill(0));
            currentPlayer = 1;
            gameOver = false;
            moveHistory = [];

            document.getElementById('winner-overlay').style.display = 'none';

            const cells = document.querySelectorAll('.cell');
            cells.forEach(cell => {
                const piece = cell.querySelector('.piece');
                if (piece) piece.remove();
                cell.style.background = '';
            });

            updateStatus();
            updateUndoButton();
        }

        // æ¨¡å¼åˆ‡æ¢
        document.querySelectorAll('.mode-btn').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('.mode-btn').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                gameMode = this.dataset.mode;
                restartGame();
            });
        });

        // åˆå§‹åŒ–æ¸¸æˆ
        initBoard();
        updateStatus();
    </script>
