<script setup lang="ts">
import { ref, onMounted, computed } from 'vue'
import { useRouter } from 'vue-router'

interface SharedQuestion {
  id: string
  title: string
  question: string
  report_content?: string
  author_id: string
  author_name?: string
  created_at: string
  updated_at: string
  likes_count: number
  views_count: number
  tags: string[]
  is_public: boolean
}

// Props
defineProps<{
  isCollapsed: boolean
}>()

// å“åº”å¼æ•°æ®
const router = useRouter()
const questions = ref<SharedQuestion[]>([])
const loading = ref(false)
const searchQuery = ref('')
const sortBy = ref('latest')
const selectedQuestionId = ref<string | null>(null)
const currentPage = ref(1)
const hasMore = ref(true)
const pageSize = 20

// è®¡ç®—å±æ€§
const filteredQuestions = computed(() => {
  if (!searchQuery.value) return questions.value
  const query = searchQuery.value.toLowerCase()
  return questions.value.filter(q => 
    q.title.toLowerCase().includes(query) || 
    q.question.toLowerCase().includes(query) ||
    q.tags.some(tag => tag.toLowerCase().includes(query))
  )
})

// æ–¹æ³•
const loadQuestions = async (reset = true) => {
  if (loading.value) return

  loading.value = true

  try {
    if (reset) {
      currentPage.value = 1
      questions.value = []
      hasMore.value = true
    }

    const params = new URLSearchParams({
      page: currentPage.value.toString(),
      limit: pageSize.toString(),
      sort: sortBy.value,
      ...(searchQuery.value && { search: searchQuery.value })
    })

    const response = await fetch(`/backend/question-square/questions?${params}`, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
      }
    })

    if (!response.ok) {
      throw new Error('Failed to load questions')
    }

    const data = await response.json()

    if (reset) {
      questions.value = data.questions || []
    } else {
      questions.value.push(...(data.questions || []))
    }

    hasMore.value = data.has_more || false

  } catch (error) {
    console.error('Error loading questions:', error)
  } finally {
    loading.value = false
  }
}

const loadMore = async () => {
  if (!hasMore.value || loading.value) return
  currentPage.value++
  await loadQuestions(false)
}

const refreshQuestions = () => {
  loadQuestions(true)
}

const selectQuestion = (question: SharedQuestion) => {
  selectedQuestionId.value = question.id
  // è·³è½¬åˆ°é—®é¢˜è¯¦æƒ…é¡µ
  router.push(`/question-square/${question.id}`)
}

// é˜²æŠ–æœç´¢
let searchTimeout: NodeJS.Timeout
const debouncedSearch = () => {
  clearTimeout(searchTimeout)
  searchTimeout = setTimeout(() => {
    loadQuestions(true)
  }, 300)
}

// æ ¼å¼åŒ–æ—¥æœŸ
const formatDate = (dateString: string) => {
  const date = new Date(dateString)
  const now = new Date()
  const diff = now.getTime() - date.getTime()

  const minutes = Math.floor(diff / (1000 * 60))
  const hours = Math.floor(diff / (1000 * 60 * 60))
  const days = Math.floor(diff / (1000 * 60 * 60 * 24))

  if (minutes < 60) {
    return `${minutes}åˆ†é’Ÿå‰`
  } else if (hours < 24) {
    return `${hours}å°æ—¶å‰`
  } else if (days < 7) {
    return `${days}å¤©å‰`
  } else {
    return date.toLocaleDateString('zh-CN')
  }
}

// ç”Ÿå‘½å‘¨æœŸ
onMounted(() => {
  loadQuestions()
})
</script>

