<script setup lang="ts">
import { ref, onMounted } from 'vue'
import { useRouter } from 'vue-router'
import ShareQuestionDialog from '@/components/question-square/ShareQuestionDialog.vue'

interface SharedQuestion {
  id: string
  title: string
  question: string
  report_content?: string
  author_id: string
  author_name?: string
  created_at: string
  updated_at: string
  likes_count: number
  views_count: number
  tags: string[]
  is_public: boolean
  is_liked?: boolean
}

// å“åº”å¼æ•°æ®
const router = useRouter()
const questions = ref<SharedQuestion[]>([])
const loading = ref(false)
const searchQuery = ref('')
const sortBy = ref('latest')
const selectedTag = ref('')
const currentPage = ref(1)
const hasMore = ref(true)
const pageSize = 20
const showShareDialog = ref(false)

// çƒ­é—¨æ ‡ç­¾
const popularTags = ref([
  'AI', 'æœºå™¨å­¦ä¹ ', 'æ·±åº¦å­¦ä¹ ', 'è‡ªç„¶è¯­è¨€å¤„ç†', 'è®¡ç®—æœºè§†è§‰',
  'æ•°æ®ç§‘å­¦', 'ç®—æ³•', 'ç¼–ç¨‹', 'æŠ€æœ¯è¶‹åŠ¿', 'ç ”ç©¶æ–¹æ³•'
])

// æ–¹æ³•
const loadQuestions = async (reset = true) => {
  if (loading.value) return

  loading.value = true

  try {
    if (reset) {
      currentPage.value = 1
      questions.value = []
      hasMore.value = true
    }

    const params = new URLSearchParams({
      page: currentPage.value.toString(),
      limit: pageSize.toString(),
      sort: sortBy.value,
      ...(searchQuery.value && { search: searchQuery.value }),
      ...(selectedTag.value && { tag: selectedTag.value })
    })

    const response = await fetch(`/backend/question-square/questions?${params}`, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
      }
    })

    if (!response.ok) {
      throw new Error('Failed to load questions')
    }

    const data = await response.json()

    if (reset) {
      questions.value = data.questions || []
    } else {
      questions.value.push(...(data.questions || []))
    }

    hasMore.value = data.has_more || false

  } catch (error) {
    console.error('Error loading questions:', error)
  } finally {
    loading.value = false
  }
}

const loadMore = async () => {
  if (!hasMore.value || loading.value) return
  currentPage.value++
  await loadQuestions(false)
}

const refreshQuestions = () => {
  loadQuestions(true)
}

const goToQuestionDetail = (questionId: string) => {
  router.push(`/question-square/${questionId}`)
}

const toggleLike = async (question: SharedQuestion) => {
  try {
    const response = await fetch(`/backend/question-square/questions/${question.id}/like`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        'Content-Type': 'application/json'
      }
    })

    if (response.ok) {
      const data = await response.json()
      question.is_liked = data.is_liked
      question.likes_count = data.likes_count
    }
  } catch (error) {
    console.error('Error toggling like:', error)
  }
}

const shareQuestion = async (question: SharedQuestion) => {
  try {
    const shareUrl = `${window.location.origin}/question-square/${question.id}`
    await navigator.clipboard.writeText(shareUrl)
    // è¿™é‡Œå¯ä»¥æ·»åŠ ä¸€ä¸ªæç¤ºæ¶ˆæ¯
    console.log('åˆ†äº«é“¾æ¥å·²å¤åˆ¶åˆ°å‰ªè´´æ¿')
  } catch (error) {
    console.error('Error sharing question:', error)
  }
}

const filterByTag = (tag: string) => {
  selectedTag.value = tag
  loadQuestions(true)
}

const onShareSuccess = () => {
  showShareDialog.value = false
  refreshQuestions()
}

// é˜²æŠ–æœç´¢
let searchTimeout: NodeJS.Timeout
const debouncedSearch = () => {
  clearTimeout(searchTimeout)
  searchTimeout = setTimeout(() => {
    loadQuestions(true)
  }, 300)
}

// æ ¼å¼åŒ–æ—¥æœŸ
const formatDate = (dateString: string) => {
  const date = new Date(dateString)
  const now = new Date()
  const diff = now.getTime() - date.getTime()

  const minutes = Math.floor(diff / (1000 * 60))
  const hours = Math.floor(diff / (1000 * 60 * 60))
  const days = Math.floor(diff / (1000 * 60 * 60 * 24))

  if (minutes < 60) {
    return `${minutes}åˆ†é’Ÿå‰`
  } else if (hours < 24) {
    return `${hours}å°æ—¶å‰`
  } else if (days < 7) {
    return `${days}å¤©å‰`
  } else {
    return date.toLocaleDateString('zh-CN')
  }
}

// ç”Ÿå‘½å‘¨æœŸ
onMounted(() => {
  loadQuestions()
})
</script>

