    return marked(question.value.report_content)
  } catch (err) {
    console.error('Error parsing markdown:', err)
    // å¦‚æœè§£æå¤±è´¥ï¼Œè¿”å›åŸå§‹å†…å®¹ï¼ˆè½¬ä¹‰HTMLï¼‰
    return question.value.report_content
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/\n/g, '<br>')
  }
})

// æ–¹æ³•
const loadQuestion = async () => {
  const questionId = route.params.id as string
  if (!questionId) {
    error.value = 'é—®é¢˜IDæ— æ•ˆ'
    return
  }

  loading.value = true
  error.value = ''

  try {
    const response = await fetch(`/backend/question-square/questions/${questionId}`, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
      }
    })

    if (!response.ok) {
      if (response.status === 404) {
        throw new Error('é—®é¢˜ä¸å­˜åœ¨')
      }
      throw new Error('åŠ è½½é—®é¢˜å¤±è´¥')
    }

    const data = await response.json()
    question.value = data.question
    relatedQuestions.value = data.related_questions || []

    // å¢åŠ æµè§ˆæ¬¡æ•°
    incrementViewCount(questionId)

  } catch (err: any) {
    error.value = err.message || 'åŠ è½½é—®é¢˜å¤±è´¥'
  } finally {
    loading.value = false
  }
}

const incrementViewCount = async (questionId: string) => {
  try {
    await fetch(`/backend/question-square/questions/${questionId}/view`, {
      method: 'POST',
