    showToast('å¤åˆ¶å¤±è´¥ï¼Œè¯·æ‰‹åŠ¨å¤åˆ¶é“¾æ¥')
  }
}

const exportReport = () => {
  if (!question.value?.report_content) return

  const content = `# ${question.value.title}\n\n## ç ”ç©¶é—®é¢˜\n\n${question.value.question}\n\n## ç ”ç©¶æŠ¥å‘Š\n\n${question.value.report_content}`
  const blob = new Blob([content], { type: 'text/markdown' })
  const url = URL.createObjectURL(blob)
  const a = document.createElement('a')
  a.href = url
  a.download = `${question.value.title}.md`
  document.body.appendChild(a)
  a.click()
  document.body.removeChild(a)
  URL.revokeObjectURL(url)

  showToast('æŠ¥å‘Šå·²å¯¼å‡º')
}

const copyReport = async () => {
  if (!question.value?.report_content) return

