const sessions = ref<ResearchSession[]>([])
const filteredSessions = ref<ResearchSession[]>([])
const selectedSession = ref<ResearchSession | null>(null)
const loadingSessions = ref(false)
const sessionSearchQuery = ref('')
const submitting = ref(false)
const shareResult = ref<ShareResult | null>(null)
const newTag = ref('')

const shareForm = ref<ShareForm>({
  title: '',
  question: '',
  tags: [],
  isPublic: true
})

const recommendedTags = [
  'AI', 'æœºå™¨å­¦ä¹ ', 'æ·±åº¦å­¦ä¹ ', 'è‡ªç„¶è¯­è¨€å¤„ç†', 'è®¡ç®—æœºè§†è§‰',
  'æ•°æ®ç§‘å­¦', 'ç®—æ³•', 'ç¼–ç¨‹', 'æŠ€æœ¯è¶‹åŠ¿', 'ç ”ç©¶æ–¹æ³•'
]

// è®¡ç®—å±æ€§
const canProceed = computed(() => {
  if (step.value === 1) {
    return selectedSession.value !== null
  } else if (step.value === 2) {
    return shareForm.value.title.trim() !== ''
  }
  return true
})

// æ–¹æ³•
const loadSessions = async () => {
  loadingSessions.value = true

  try {
    const response = await fetch('/backend/conversation/research/sessions', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
      }
    })

    if (response.ok) {
      const data = await response.json()
      sessions.value = data.sessions || []
      filteredSessions.value = sessions.value
    }
  } catch (error) {
    console.error('Error loading sessions:', error)
  } finally {
    loadingSessions.value = false
  }
}

const searchSessions = () => {
  const query = sessionSearchQuery.value.toLowerCase()
  if (!query) {
    filteredSessions.value = sessions.value
  } else {
    filteredSessions.value = sessions.value.filter(session => 
      (session.name || '').toLowerCase().includes(query) ||
      session.question.toLowerCase().includes(query)
    )
  }
}

const selectSession = (session: ResearchSession) => {
  selectedSession.value = session
  // è‡ªåŠ¨å¡«å……è¡¨å•
  shareForm.value.title = session.name || 'æ·±åº¦ç ”ç©¶åˆ†äº«'
  shareForm.value.question = session.question
}

const nextStep = async () => {
  if (step.value === 2) {
    // ç›´æ¥è¿›å…¥ç¡®è®¤æ­¥éª¤
    step.value = 3
  } else {
    step.value++
  }
}

const previousStep = () => {
  if (step.value > 1) {
    step.value--
    shareResult.value = null
  }
}

const addTag = () => {
  const tag = newTag.value.trim()
  if (tag && !shareForm.value.tags.includes(tag)) {
    shareForm.value.tags.push(tag)
    newTag.value = ''
  }
}

const addRecommendedTag = (tag: string) => {
  if (!shareForm.value.tags.includes(tag)) {
    shareForm.value.tags.push(tag)
  }
}

const removeTag = (tag: string) => {
  const index = shareForm.value.tags.indexOf(tag)
  if (index > -1) {
    shareForm.value.tags.splice(index, 1)
  }
}

const submitShare = async () => {
  if (!selectedSession.value) return

  submitting.value = true
  shareResult.value = null

  try {
    const response = await fetch('/backend/question-square/share', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
      },
      body: JSON.stringify({
        session_id: selectedSession.value.id,
        title: shareForm.value.title,
        question: shareForm.value.question || selectedSession.value.question,
        tags: shareForm.value.tags,
        is_public: shareForm.value.isPublic
      })
    })

    const data = await response.json()

    if (response.ok) {
      shareResult.value = {
        success: true,
        message: 'åˆ†äº«æˆåŠŸï¼',
        shareUrl: `${window.location.origin}/question-square/${data.question_id}`
      }
      emit('success')
    } else {
      shareResult.value = {
        success: false,
        message: data.detail || 'åˆ†äº«å¤±è´¥ï¼Œè¯·é‡è¯•'
      }
    }
  } catch (error) {
    console.error('Error sharing question:', error)
    shareResult.value = {
      success: false,
      message: 'ç½‘ç»œé”™è¯¯ï¼Œè¯·é‡è¯•'
    }
  } finally {
    submitting.value = false
  }
}

const copyShareUrl = async () => {
  if (!shareResult.value?.shareUrl) return

  try {
    await navigator.clipboard.writeText(shareResult.value.shareUrl)
    // å¯ä»¥æ·»åŠ ä¸€ä¸ªæç¤º
  } catch (error) {
    console.error('Error copying URL:', error)
  }
}

const closeDialog = () => {
  emit('update:modelValue', false)
  // é‡ç½®çŠ¶æ€
  setTimeout(() => {
    step.value = 1
    selectedSession.value = null
    shareForm.value = {
      title: '',
      question: '',
      tags: [],
      isPublic: true
    }
    shareResult.value = null
    sessionSearchQuery.value = ''
    newTag.value = ''
  }, 300)
}

const formatDate = (dateString: string) => {
  const date = new Date(dateString)
  return date.toLocaleDateString('zh-CN')
}

// ç›‘å¬å¯¹è¯æ¡†æ‰“å¼€
watch(() => props.modelValue, (newValue) => {
  if (newValue) {
    loadSessions()
  }
})
</script>

