{"file_path":"/home/junfei/DAIR/backend/question_square/service.py","content":"import logging
from typing import List, Optional, Dict, Any
from datetime import datetime, timedelta
from motor.motor_asyncio import AsyncIOMotorDatabase
from pymongo import DESCENDING, ASCENDING
from bson import ObjectId
from backend.db.mongodb import get_database
from backend.question_square.models import (
    SharedQuestion, QuestionLike, QuestionView,
    ShareQuestionRequest, QuestionListRequest,
    QuestionListResponse, QuestionDetailResponse, LikeToggleResponse
)
from backend.auth.models import UserDB
from backend.conversation.session_store import SessionStore

logger = logging.getLogger(__name__)

class QuestionSquareService:
    def __init__(self):
        self.db: AsyncIOMotorDatabase = get_database()
        self.questions_collection = self.db.shared_questions
        self.likes_collection = self.db.question_likes
        self.views_collection = self.db.question_views
        self.session_store = SessionStore()

    async def share_question(
        self, 
        request: ShareQuestionRequest, 
        user: UserDB
    ) -> Dict[str, Any]:
        """åˆ†äº«é—®é¢˜åˆ°å¹¿åœº"""
        try:
            # è·å–ä¼šè¯ä¿¡æ¯å’ŒæŠ¥å‘Šå†…å®¹
            session_data = await self.session_store.get_session_data(request.session_id)
            if not session_data:
                raise ValueError("ä¼šè¯ä¸å­˜åœ¨")

            # æ£€æŸ¥ä¼šè¯æ˜¯å¦å±äºå½“å‰ç”¨æˆ·
            if session_data.user_id != user.id:
                raise ValueError("æ— æƒé™åˆ†äº«æ­¤ä¼šè¯")

            # è·å–æŠ¥å‘Šå†…å®¹
            report_content = None
            if hasattr(session_data, 'report_content') and session_data.report_content:
                report_content = session_data.report_content
            else:
                # å°è¯•ä»æ¶ˆæ¯ä¸­æå–æŠ¥å‘Šå†…å®¹
                messages = await self.session_store.get_session_messages(request.session_id)
                for message in reversed(messages):
                    if message.role == "assistant" and len(message.content) > 500:
                        # å‡è®¾è¾ƒé•¿çš„åŠ©æ‰‹å›å¤æ˜¯æŠ¥å‘Šå†…å®¹
                        report_content = message.content
                        break

            # åˆ›å»ºåˆ†äº«é—®é¢˜
            shared_question = SharedQuestion(
                title=request.title,
                question=request.question,
                report_content=report_content,
                session_id=request.session_id,
                author_id=user.id,
                author_name=user.username or user.email,
                tags=request.tags,
                is_public=request.is_public
            )

            # ä¿å­˜åˆ°æ•°æ®åº“
            result = await self.questions_collection.insert_one(
                shared_question.dict(by_alias=True, exclude={"id"})
            )

            return {
                "question_id": str(result.inserted_id),
                "message": "é—®é¢˜åˆ†äº«æˆåŠŸ"
            }

        except Exception as e:
            logger.error(f"Error sharing question: {e}")
            raise

    async def get_questions_list(
        self, 
        request: QuestionListRequest,
        user: Optional[UserDB] = None
    ) -> QuestionListResponse:
        """è·å–é—®é¢˜åˆ—è¡¨"""
        try:
            # æ„å»ºæŸ¥è¯¢æ¡ä»¶
            query = {"is_public": True}  # åªæ˜¾ç¤ºå…¬å¼€çš„é—®é¢˜

            # æœç´¢æ¡ä»¶
            if request.search:
                search_regex = {"$regex": request.search, "$options": "i"}
                query["$or"] = [
                    {"title": search_regex},
                    {"question": search_regex},
                    {"tags": search_regex}
                ]

            # æ ‡ç­¾ç­›é€‰
            if request.tag:
                query["tags"] = request.tag

            # ä½œè€…ç­›é€‰
            if request.author_id:
                query["author_id"] = request.author_id

            # æ’åºæ¡ä»¶
            sort_options = {
                "latest": [("created_at", DESCENDING)],
                "popular": [("likes_count", DESCENDING), ("created_at", DESCENDING)],
                "trending": [("likes_count", DESCENDING), ("views_count", DESCENDING)],
                "most_viewed": [("views_count", DESCENDING), ("created_at", DESCENDING)]
            }
            sort = sort_options.get(request.sort, [("created_at", DESCENDING)])

            # åˆ†é¡µ
            skip = (request.page - 1) * request.limit

            # æŸ¥è¯¢æ€»æ•°
            total = await self.questions_collection.count_documents(query)

            # æŸ¥è¯¢é—®é¢˜åˆ—è¡¨
            cursor = self.questions_collection.find(query).sort(sort).skip(skip).limit(request.limit)
            questions_data = await cursor.to_list(length=request.limit)

            # å¤„ç†é—®é¢˜æ•°æ®
            questions = []
            user_id = user.id if user else None

            for question_data in questions_data:
                question_dict = self._format_question_dict(question_data)

                # æ£€æŸ¥å½“å‰ç”¨æˆ·æ˜¯å¦ç‚¹èµ
                if user_id:
                    like_exists = await self.likes_collection.find_one({
                        "question_id": str(question_data["_id"]),
                        "user_id": user_id
                    })
                    question_dict["is_liked"] = like_exists is not None
                else:
                    question_dict["is_liked"] = False

                questions.append(question_dict)

            # è®¡ç®—æ˜¯å¦æœ‰æ›´å¤š
            has_more = skip + len(questions) < total

            return QuestionListResponse(
                questions=questions,
                total=total,
                page=request.page,
                limit=request.limit,
                has_more=has_more
            )

        except Exception as e:
            logger.error(f"Error getting questions list: {e}")
            raise

    async def get_question_detail(
        self, 
        question_id: str,
        user: Optional[UserDB] = None
    ) -> QuestionDetailResponse:
        """è·å–é—®é¢˜è¯¦æƒ…"""
        try:
            # éªŒè¯ObjectIdæ ¼å¼
            if not ObjectId.is_valid(question_id):
                raise ValueError("æ— æ•ˆçš„é—®é¢˜ID")

            # æŸ¥è¯¢é—®é¢˜
            question_data = await self.questions_collection.find_one({
                "_id": ObjectId(question_id),
                "is_public": True
            })

            if not question_data:
                raise ValueError("é—®é¢˜ä¸å­˜åœ¨")

            # æ ¼å¼åŒ–é—®é¢˜æ•°æ®
            question_dict = self._format_question_dict(question_data)

            # æ£€æŸ¥å½“å‰ç”¨æˆ·æ˜¯å¦ç‚¹èµ
            user_id = user.id if user else None
            if user_id:
                like_exists = await self.likes_collection.find_one({
                    "question_id": question_id,
                    "user_id": user_id
                })
                question_dict["is_liked"] = like_exists is not None
            else:
                question_dict["is_liked"] = False

            # è·å–ç›¸å…³é—®é¢˜ï¼ˆåŸºäºæ ‡ç­¾ï¼‰
            related_questions = await self._get_related_questions(
                question_data.get("tags", []), 
                question_id,
                user_id
            )

            return QuestionDetailResponse(
                question=question_dict,
                related_questions=related_questions
            )

        except Exception as e:
            logger.error(f"Error getting question detail: {e}")
            raise

    async def toggle_like(
        self, 
        question_id: str, 
        user: UserDB
    ) -> LikeToggleResponse:
        """åˆ‡æ¢é—®é¢˜ç‚¹èµçŠ¶æ€"""
        try:
            # éªŒè¯ObjectIdæ ¼å¼
            if not ObjectId.is_valid(question_id):
                raise ValueError("æ— æ•ˆçš„é—®é¢˜ID")

            # æ£€æŸ¥é—®é¢˜æ˜¯å¦å­˜åœ¨
            question_exists = await self.questions_collection.find_one({
                "_id": ObjectId(question_id),
                "is_public": True
            })
            if not question_exists:
                raise ValueError("é—®é¢˜ä¸å­˜åœ¨")

            # æ£€æŸ¥æ˜¯å¦å·²ç‚¹èµ
            existing_like = await self.likes_collection.find_one({
                "question_id": question_id,
                "user_id": user.id
            })

            if existing_like:
                # å–æ¶ˆç‚¹èµ
                await self.likes_collection.delete_one({
                    "question_id": question_id,
                    "user_id": user.id
                })
                # å‡å°‘ç‚¹èµæ•°
                await self.questions_collection.update_one(
                    {"_id": ObjectId(question_id)},
                    {"$inc": {"likes_count": -1}}
                )
                is_liked = False
            else:
                # æ·»åŠ ç‚¹èµ
                like = QuestionLike(
                    question_id=question_id,
                    user_id=user.id
                )
                await self.likes_collection.insert_one(
                    like.dict(by_alias=True, exclude={"id"})
                )
                # å¢åŠ ç‚¹èµæ•°
                await self.questions_collection.update_one(
                    {"_id": ObjectId(question_id)},
                    {"$inc": {"likes_count": 1}}
                )
                is_liked = True

            # è·å–æœ€æ–°ç‚¹èµæ•°
            updated_question = await self.questions_collection.find_one(
                {"_id": ObjectId(question_id)}
            )
            likes_count = updated_question.get("likes_count", 0)

            return LikeToggleResponse(
                is_liked=is_liked,
                likes_count=likes_count
            )

        except Exception as e:
            logger.error(f"Error toggling like: {e}")
            raise

    async def record_view(
        self, 
        question_id: str,
        user: Optional[UserDB] = None,
        ip_address: Optional[str] = None,
        user_agent: Optional[str] = None
    ):
        """è®°å½•é—®é¢˜æµè§ˆ"""
        try:
            # éªŒè¯ObjectIdæ ¼å¼
            if not ObjectId.is_valid(question_id):
                return

            # æ£€æŸ¥é—®é¢˜æ˜¯å¦å­˜åœ¨
            question_exists = await self.questions_collection.find_one({
                "_id": ObjectId(question_id),
                "is_public": True
            })
            if not question_exists:
                return

            user_id = user.id if user else None

            # æ£€æŸ¥æ˜¯å¦åœ¨çŸ­æ—¶é—´å†…é‡å¤æµè§ˆï¼ˆé˜²æ­¢åˆ·æµè§ˆé‡ï¼‰
            recent_view_query = {
                "question_id": question_id,
                "created_at": {"$gte": datetime.utcnow() - timedelta(hours=1)}
            }

            if user_id:
                recent_view_query["user_id"] = user_id
            elif ip_address:
                recent_view_query["ip_address"] = ip_address

            recent_view = await self.views_collection.find_one(recent_view_query)
            if recent_view:
                return  # çŸ­æ—¶é—´å†…é‡å¤æµè§ˆï¼Œä¸è®°å½•

            # è®°å½•æµè§ˆ
            view = QuestionView(
                question_id=question_id,
                user_id=user_id,
                ip_address=ip_address,
                user_agent=user_agent
            )
            await self.views_collection.insert_one(
                view.dict(by_alias=True, exclude={"id"})
            )

            # å¢åŠ æµè§ˆæ•°
            await self.questions_collection.update_one(
                {"_id": ObjectId(question_id)},
                {"$inc": {"views_count": 1}}
            )

        except Exception as e:
            logger.error(f"Error recording view: {e}")

    async def get_user_questions(
        self, 
        user_id: str,
        page: int = 1,
        limit: int = 20
    ) -> List[Dict[str, Any]]:
        """è·å–ç”¨æˆ·åˆ†äº«çš„é—®é¢˜"""
        try:
            skip = (page - 1) * limit

            cursor = self.questions_collection.find({
                "author_id": user_id
            }).sort([("created_at", DESCENDING)]).skip(skip).limit(limit)

            questions_data = await cursor.to_list(length=limit)

            questions = []
            for question_data in questions_data:
                question_dict = self._format_question_dict(question_data)
                questions.append(question_dict)

            return questions

        except Exception as e:
            logger.error(f"Error getting user questions: {e}")
            raise

    async def delete_question(self, question_id: str, user: UserDB):
        """åˆ é™¤é—®é¢˜ï¼ˆä»…ä½œè€…å¯åˆ é™¤ï¼‰"""
        try:
            # éªŒè¯ObjectIdæ ¼å¼
            if not ObjectId.is_valid(question_id):
                raise ValueError("æ— æ•ˆçš„é—®é¢˜ID")

            # æ£€æŸ¥é—®é¢˜æ˜¯å¦å­˜åœ¨ä¸”å±äºå½“å‰ç”¨æˆ·
            question = await self.questions_collection.find_one({
                "_id": ObjectId(question_id),
                "author_id": user.id
            })
            if not question:
                raise ValueError("é—®é¢˜ä¸å­˜åœ¨æˆ–æ— æƒé™åˆ é™¤")

            # åˆ é™¤é—®é¢˜
            await self.questions_collection.delete_one({"_id": ObjectId(question_id)})

            # åˆ é™¤ç›¸å…³çš„ç‚¹èµå’Œæµè§ˆè®°å½•
            await self.likes_collection.delete_many({"question_id": question_id})
            await self.views_collection.delete_many({"question_id": question_id})

        except Exception as e:
            logger.error(f"Error deleting question: {e}")
            raise

    def _format_question_dict(self, question_data: Dict[str, Any]) -> Dict[str, Any]:
        """æ ¼å¼åŒ–é—®é¢˜æ•°æ®"""
        return {
            "id": str(question_data["_id"]),
            "title": question_data.get("title", ""),
            "question": question_data.get("question", ""),
            "report_content": question_data.get("report_content"),
            "author_id": question_data.get("author_id", ""),
            "author_name": question_data.get("author_name"),
            "tags": question_data.get("tags", []),
            "is_public": question_data.get("is_public", True),
            "likes_count": question_data.get("likes_count", 0),
            "views_count": question_data.get("views_count", 0),
            "created_at": question_data.get("created_at").isoformat() if question_data.get("created_at") else None,
            "updated_at": question_data.get("updated_at").isoformat() if question_data.get("updated_at") else None
        }

    async def _get_related_questions(
        self, 
        tags: List[str], 
        exclude_id: str,
        user_id: Optional[str] = None,
        limit: int = 4
    ) -> List[Dict[str, Any]]:
        """è·å–ç›¸å…³é—®é¢˜"""
        try:
            if not tags:
                return []

            # æŸ¥è¯¢æœ‰ç›¸åŒæ ‡ç­¾çš„é—®é¢˜
            query = {
                "tags": {"$in": tags},
                "_id": {"$ne": ObjectId(exclude_id)},
                "is_public": True
            }

            cursor = self.questions_collection.find(query).sort([
                ("likes_count", DESCENDING),
                ("created_at", DESCENDING)
            ]).limit(limit)

            questions_data = await cursor.to_list(length=limit)

            questions = []
            for question_data in questions_data:
                question_dict = self._format_question_dict(question_data)

                # æ£€æŸ¥å½“å‰ç”¨æˆ·æ˜¯å¦ç‚¹èµ
                if user_id:
                    like_exists = await self.likes_collection.find_one({
                        "question_id": str(question_data["_id"]),
                        "user_id": user_id
                    })
                    question_dict["is_liked"] = like_exists is not None
                else:
                    question_dict["is_liked"] = False

                questions.append(question_dict)

            return questions

        except Exception as e:
            logger.error(f"Error getting related questions: {e}")
            return []

# å•ä¾‹æœåŠ¡å®ä¾‹
_question_square_service = None

def get_question_square_service() -> QuestionSquareService:
    """è·å–é—®é¢˜å¹¿åœºæœåŠ¡å®ä¾‹"""
    global _question_square_service
    if _question_square_service is None:
        _question_square_service = QuestionSquareService()
    return _question_square_service"}

