  message: string;
}

// 创建 axios 实例 - 使用相对路径让Vite处理代理
const api = axios.create({
  baseURL: '/backend',
  headers: {
    'Content-Type': 'application/json',
  },
});



// 设置请求拦截器，添加 token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('auth_token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// 存储活跃的WebSocket连接
const activeWebSockets: Record<string, WebSocket> = {};

// 聊天 API 方法
export const chatApi = {
  // 普通聊天
  chat: async (data: ChatRequest): Promise<ChatResponse> => {
    const response = await api.post('/api/conversations/chat', data);
    return response.data;
  },

  // Markdown转PDF
  markdownToPdf: async (markdownContent: string, filename: string = 'document.pdf'): Promise<Blob> => {
    const response = await api.post('/api/markdown-to-pdf', {
      markdown_content: markdownContent,
      filename: filename
    }, {
      responseType: 'blob'  // 指定响应类型为blob
    });
    return response.data;
  },

  // 提交编辑请求（chat to edit 功能）
  submitEditRequest: async (sessionId: string, editData: { edit_content: string, edit_type: string }): Promise<any> => {
    const response = await api.post(`/conversation/research/${sessionId}/edit_request`, editData);
    return response.data;
  },

  // OpenAI 兼容的聊天完成接口
  openaiChatCompletions: async (data: any): Promise<any> => {
    const response = await api.post('/engineer/v1/chat/completions', data);
    return response.data;
  },

  // 直接调用OpenRouter
  callOpenRouter: async (prompt: string): Promise<any> => {
    // 这是一个特殊的、直接的前端到外部服务的调用，因此不使用全局的axios实例
    const response = await axios.post(
      "https://openrouter.ai/api/v1/chat/completions",
      {
        model: "anthropic/claude-sonnet-4",
        messages: [
          {
            role: "user",
            content: [
              {
                type: "text",
                text: prompt,
                cache_control: {
                  type: "ephemeral"
                }
              }
            ]
          }
        ],
        temperature: 0.6,
        max_tokens: 1000
      },
      {
        headers: {
          "Authorization": "Bearer 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",
          "Content-Type": "application/json"
        }
      }
    );
    return response.data;
  },
};

// 研究API
export const researchApi = {
  // 获取当前用户的所有对话
  getMyChats: async (skip: number = 0, limit: number = 20): Promise<any> => {
    const response = await api.get('/conversation/api/chats/my', {
      params: { skip, limit }
    });
    return response.data || [];
  },

  // 启动研究任务
  startResearch: async (data: ResearchRequest): Promise<ResearchResponse> => {
    const response = await api.post('/conversation/research/start', data);
    return response.data;
  },

  // 获取研究状态
  getResearchStatus: async (sessionId: string): Promise<any> => {
    const response = await api.get(`/conversation/research/${sessionId}/status`);
    return response.data;
  },

  // 获取V2会话的消息历史
  getSessionMessages: async (sessionId: string): Promise<any> => {
    const response = await api.get(`/conversation/sessions/${sessionId}/messages`);
    return response.data;
  },

  getAvailableModels: async (): Promise<any> => {
    const response = await api.get('/conversation/api/models/available');
