  },
  setModelsPreferences: async (preferences: any): Promise<any> => {
    const response = await api.post('/conversation/api/models/preferences', preferences);
    return response.data;
  },

  // 提交用户反馈
  submitFeedback: async (sessionId: string, feedback: string): Promise<any> => {
    const response = await api.post(`/conversation/research/${sessionId}/feedback`, {
      feedback: feedback
    });
    return response.data;
  },

  // 提交澄清问题回答
  submitClarificationAnswer: async (sessionId: string, answers: Array<{ id: number, answer: string }>): Promise<any> => {
    const response = await api.post(`/conversation/research/${sessionId}/clarification_answer`, {
      answers: answers // 直接传递完整的answers对象数组，包含id和answer
    });
    return response.data;
  },

  // 暂停研究任务
  pauseResearch: async (sessionId: string): Promise<any> => {
    const response = await api.post(`/conversation/research/${sessionId}/pause`);
    return response.data;
  },

  // 恢复研究任务
  resumeResearch: async (sessionId: string): Promise<any> => {
    const response = await api.post(`/conversation/research/${sessionId}/resume`);
    return response.data;
  },
  getPendingQuestions: async (sessionId: string): Promise<any> => {
    const response = await api.get(`/conversation/research/${sessionId}/pending_questions`);
    return response.data;
  },

  // 连接WebSocket获取实时研究进展
  connectClientWebSocket: (clientId: string, handlers: WebSocketHandlers): WebSocket => {
    // 关闭已存在的连接
    if (activeWebSockets[clientId]) {
      console.log(`关闭已存在的WebSocket连接: ${clientId}`);
      activeWebSockets[clientId].close();
    }

    // 1. 从localStorage获取认证令牌
    const token = localStorage.getItem('auth_token');

    if (!token) {
      console.error("认证令牌未找到，无法建立WebSocket连接。请确保用户已登录。");
      // 返回一个立即关闭的WebSocket或抛出错误，以防止无效连接
      const dummySocket = new WebSocket("wss://echo.websocket.org"); // A valid but useless endpoint
      setTimeout(() => dummySocket.close(4001, "No auth token"), 50);
      return dummySocket;
    }

    // 2. 创建WebSocket连接 - 将token作为查询参数
    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const wsUrl = `${protocol}//${window.location.host}/conversation/ws/${clientId}?token=${token}`;
    console.log(`正在连接客户端WebSocket...`); // 从日志中隐藏URL以保护token

    const socket = new WebSocket(wsUrl);

    // 设置连接事件处理
    socket.onopen = (event) => {
      console.log(`客户端WebSocket连接已建立: ${clientId}`);
      handlers.onOpen && handlers.onOpen(event);
    };

    // 设置事件处理程序
    socket.onmessage = (event) => {
      // console.log(`WebSocket收到消息: ${sessionId}`, event.data.substring(0, 100) + (event.data.length > 100 ? '...' : ''));
      try {
        const data = JSON.parse(event.data);
        // console.log(`WebSocket消息解析成功: ${clientId}, 类型=${data.type || 'unknown'}`);

        // 其他消息传递给处理器
        handlers.onMessage(data);
      } catch (error) {
        console.error('WebSocket消息解析错误:', error);
        handlers.onError(error);
      }
    };

    socket.onerror = (error) => {
      // 打印错误的详细信息
      console.error(`WebSocket错误: ${clientId}`, {
        error: error,
        errorType: error instanceof Error ? error.name : typeof error,
        errorMessage: error instanceof Error ? error.message : String(error),
        errorStack: error instanceof Error ? error.stack : null,
        readyState: socket.readyState,
        url: socket.url,
        protocol: socket.protocol,
        extensions: socket.extensions,
        bufferedAmount: socket.bufferedAmount
      });

      // 分析可能的错误原因
      let errorAnalysis = '可能的错误原因:\n';
      if (socket.readyState === WebSocket.CLOSED) {
        errorAnalysis += '- WebSocket连接已关闭\n';
      }
      if (socket.readyState === WebSocket.CONNECTING) {
        errorAnalysis += '- WebSocket连接尚未建立，可能是网络问题\n';
      }
      if (!navigator.onLine) {
        errorAnalysis += '- 浏览器当前处于离线状态\n';
      }
      console.error(errorAnalysis);

      handlers.onError(error);
    };

    socket.onclose = (event) => {
      console.log(`客户端WebSocket连接已关闭: ${clientId}, 代码=${event.code}, 原因=${event.reason || '未提供'}`);
      delete activeWebSockets[clientId];
      handlers.onClose(event);
    };

    // 保存连接
    activeWebSockets[clientId] = socket;

    return socket;
  },

  // 断开WebSocket连接
  disconnectWebSocket: (clientId: string): void => {
    if (activeWebSockets[clientId]) {
      activeWebSockets[clientId].close();
      delete activeWebSockets[clientId];
    }
  },


  // 获取分享的研究内容
  getSharedResearch: async (conversationId: string): Promise<any> => {
    const response = await api.get(`/conversation/api/research/share/${conversationId}`);
