  // 上传文件到研究会话
  uploadFile: async (conversationId: string, file: File): Promise<any> => {
    const formData = new FormData();
    formData.append('conversation_id', conversationId);
    formData.append('file', file);
    formData.append('file_type', file.type);

    const response = await api.post('/conversation/v2/research/upload_file', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    });
    return response.data;
  },

  // 一句话识别：仅转写
  asrShort: async (file: File, language: string = 'zh'): Promise<{ text: string }> => {
    console.log('[API] asrShort 请求开始，URL: /conversation/api/v2/asr/short');
    const formData = new FormData();
    formData.append('file', file);
    formData.append('language', language);
    console.log('[API] FormData 准备完成，文件:', file.name, '语言:', language);

    try {
      const response = await api.post('/conversation/api/v2/asr/short', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      console.log('[API] asrShort 响应:', response.status, response.data);
      return response.data;
    } catch (error) {
      console.error('[API] asrShort 请求失败:', error);
      throw error;
    }
  },

  // 一句话识别：转写并注入为 question
  asrShortToQuestion: async (sessionId: string, file: File, language: string = 'zh'): Promise<{ text: string }> => {
    const formData = new FormData();
