  },

  // 提交内容偏好（点赞/取消点赞）
  submitContentPreference: async (data: {
    conversation_id: string;
    url: string;
    title: string;
    liked: boolean;
    timestamp?: string;
  }): Promise<any> => {
    const response = await api.post('/conversation/api/research/content_preference', data);
    return response.data;
  },

  // 设置会话模式
  setMode: async (sessionId: string, mode: 'dc' | 'llm'): Promise<any> => {
    const response = await api.post('/conversation/api/v2/research/set_mode', {
      session_id: sessionId,
      mode: mode,
    });
    return response.data;
  },

  // 发送聊天消息
  sendChatMessage: async (sessionId: string, message: string): Promise<any> => {
    const response = await api.post('/conversation/api/v2/chat/send', {
      session_id: sessionId,
      message: message,
    });
    return response.data;
  },

  startPoeChat: async (sessionId: string, message: string): Promise<any> => {
