import axios from 'axios';

// 定义接口类型
export interface QuestionPost {
  id: string;
  title: string;
  conversation_id: string;
  user_id: string;
  username: string;
  description?: string;
  tags?: string;
  view_count: number;
  like_count: number;
  comment_count: number;
  is_active: boolean;
  is_featured: boolean;
  created_at: string;
  updated_at: string;
  is_liked: boolean;
}

export interface PostComment {
  id: string;
  post_id: string;
  user_id: string;
  username: string;
  content: string;
  parent_id?: string;
  like_count: number;
  is_active: boolean;
  created_at: string;
  updated_at: string;
  is_liked: boolean;
  replies: PostComment[];
}

export interface QuestionPostDetail extends QuestionPost {
  comments: PostComment[];
}

export interface PostListResponse {
  posts: QuestionPost[];
  total: number;
  page: number;
  page_size: number;
  total_pages: number;
}

export interface CreatePostRequest {
  title: string;
  conversation_id: string;
  description?: string;
  tags?: string;
}

export interface UpdatePostRequest {
  title?: string;
  description?: string;
  tags?: string;
  is_featured?: boolean;
}

export interface CreateCommentRequest {
  content: string;
  parent_id?: string;
}

export interface LikeResponse {
  success: boolean;
  is_liked: boolean;
  like_count: number;
}

export interface UserConversation {
  conversation_id: string;
  prompt: string;
  created_at: string;
}

export interface UserConversationsResponse {
  conversations: UserConversation[];
  total: number;
}

// 创建 axios 实例
const api = axios.create({
  baseURL: '/backend',
  headers: {
    'Content-Type': 'application/json',
  },
});

// 设置请求拦截器，添加 token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('auth_token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// 问题广场 API 方法
export const questionSquareApi = {
  // 创建帖子
  createPost: async (data: CreatePostRequest): Promise<QuestionPost> => {
    const response = await api.post('/api/question-square/posts', data);
    return response.data;
  },

  // 获取帖子列表
  getPosts: async (params: {
    page?: number;
    page_size?: number;
    sort_by?: 'latest' | 'hot' | 'most_liked';
    search?: string;
    tags?: string;
  } = {}): Promise<PostListResponse> => {
    const response = await api.get('/api/question-square/posts', { params });
    return response.data;
  },

  // 获取帖子详情
  getPostDetail: async (postId: string): Promise<QuestionPostDetail> => {
    const response = await api.get(`/api/question-square/posts/${postId}`);
    return response.data;
  },

  // 更新帖子
  updatePost: async (postId: string, data: UpdatePostRequest): Promise<QuestionPost> => {
    const response = await api.put(`/api/question-square/posts/${postId}`, data);
    return response.data;
  },

  // 删除帖子
  deletePost: async (postId: string): Promise<{ success: boolean }> => {
    const response = await api.delete(`/api/question-square/posts/${postId}`);
    return response.data;
  },

  // 切换帖子点赞
  togglePostLike: async (postId: string): Promise<LikeResponse> => {
    const response = await api.post(`/api/question-square/posts/${postId}/like`);
    return response.data;
  },

  // 创建评论
  createComment: async (postId: string, data: CreateCommentRequest): Promise<PostComment> => {
    const response = await api.post(`/api/question-square/posts/${postId}/comments`, data);
    return response.data;
  },

  // 切换评论点赞
  toggleCommentLike: async (commentId: string): Promise<LikeResponse> => {
    const response = await api.post(`/api/question-square/comments/${commentId}/like`);
    return response.data;
  },

  // 获取我的帖子
  getMyPosts: async (params: {
    page?: number;
    page_size?: number;
  } = {}): Promise<PostListResponse> => {
    const response = await api.get('/api/question-square/my-posts', { params });
    return response.data;
  },

  // 获取我的对话列表
  getMyConversations: async (): Promise<UserConversationsResponse> => {
    const response = await api.get('/api/question-square/my-conversations');
    return response.data;
  },
};

export default questionSquareApi;"}

