<script setup lang="ts">
import { ref, onMounted, computed } from 'vue'
import { useRouter } from 'vue-router'
import { questionSquareApi, type QuestionPost, type LikeResponse } from '@/api/questionSquare'
import PostCard from '@/components/question-square/PostCard.vue'
import SharePostDialog from '@/components/question-square/SharePostDialog.vue'

const router = useRouter()

// 响应式数据
const posts = ref<QuestionPost[]>([])
const loading = ref(false)
const currentPage = ref(1)
const totalPages = ref(1)
const total = ref(0)
const pageSize = ref(20)

// 筛选和排序
const sortBy = ref<'latest' | 'hot' | 'most_liked'>('latest')
const searchQuery = ref('')
const tagsFilter = ref('')

// 弹窗状态
const showShareDialog = ref(false)

// 防抖搜索
let searchTimeout: NodeJS.Timeout | null = null
const debounceSearch = () => {
  if (searchTimeout) {
    clearTimeout(searchTimeout)
  }
  searchTimeout = setTimeout(() => {
    currentPage.value = 1
    loadPosts()
  }, 500)
}

// 加载帖子列表
const loadPosts = async () => {
  try {
    loading.value = true
    const response = await questionSquareApi.getPosts({
      page: currentPage.value,
      page_size: pageSize.value,
      sort_by: sortBy.value,
      search: searchQuery.value || undefined,
      tags: tagsFilter.value || undefined
    })

    posts.value = response.posts
    total.value = response.total
    totalPages.value = response.total_pages
  } catch (error) {
    console.error('加载帖子失败:', error)
    // 这里可以添加错误提示
  } finally {
    loading.value = false
  }
}

// 切换页面
const changePage = (page: number) => {
  currentPage.value = page
  loadPosts()
}

// 处理点赞
const handleLike = async (postId: string) => {
  try {
    const response = await questionSquareApi.togglePostLike(postId)

    // 更新本地数据
    const post = posts.value.find(p => p.id === postId)
    if (post) {
      post.is_liked = response.is_liked
      post.like_count = response.like_count
    }
  } catch (error) {
    console.error('点赞失败:', error)
    // 这里可以添加错误提示
  }
}

// 跳转到帖子详情
const goToPostDetail = (post: QuestionPost) => {
  router.push(`/question-square/posts/${post.id}`)
}

// 处理分享成功
const handleShareSuccess = () => {
  showShareDialog.value = false
  // 重新加载第一页
  currentPage.value = 1
  loadPosts()
}

// 组件挂载时加载数据
onMounted(() => {
  loadPosts()
})
</script>

