{"file_path":"/home/junfei/DAIR/frontend/src/components/question-square/PostCard.vue","content":"<template>
  <div class="post-card" @click="$emit('click', post)">
    <!-- 帖子头部 -->
    <div class="post-header">
      <div class="post-meta">
        <div class="author-info">
          <div class="avatar">
            {{ post.username.charAt(0).toUpperCase() }}
          </div>
          <div class="author-details">
            <span class="username">{{ post.username }}</span>
            <span class="post-time">{{ formatTime(post.created_at) }}</span>
          </div>
        </div>

        <div v-if="post.is_featured" class="featured-badge">
          <i class="fas fa-star"></i>
          精选
        </div>
      </div>
    </div>

    <!-- 帖子内容 -->
    <div class="post-content">
      <h3 class="post-title">{{ post.title }}</h3>

      <p v-if="post.description" class="post-description">
        {{ truncateText(post.description, 200) }}
      </p>

      <!-- 标签 -->
      <div v-if="post.tags" class="tags">
        <span 
          v-for="tag in parseTags(post.tags)" 
          :key="tag"
          class="tag"
        >
          #{{ tag }}
        </span>
      </div>
    </div>

    <!-- 帖子底部 -->
    <div class="post-footer">
      <div class="stats">
        <span class="stat-item">
          <i class="fas fa-eye"></i>
          {{ post.view_count }}
        </span>

        <span class="stat-item">
          <i class="fas fa-comment"></i>
          {{ post.comment_count }}
        </span>
      </div>

      <div class="actions">
        <button 
          class="action-btn like-btn"
          :class="{ 'liked': post.is_liked }"
          @click.stop="$emit('like', post.id)"
        >
          <i class="fas fa-heart"></i>
          {{ post.like_count }}
        </button>

        <button 
          class="action-btn share-btn"
          @click.stop="handleShare"
        >
          <i class="fas fa-share"></i>
          分享
        </button>
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
import { computed } from 'vue'
import type { QuestionPost } from '@/api/questionSquare'

interface Props {
  post: QuestionPost
}

const props = defineProps<Props>()

const emit = defineEmits<{
  click: [post: QuestionPost]
  like: [postId: string]
}>()

// 格式化时间
const formatTime = (timeStr: string) => {
  const time = new Date(timeStr)
  const now = new Date()
  const diff = now.getTime() - time.getTime()

  const minutes = Math.floor(diff / (1000 * 60))
  const hours = Math.floor(diff / (1000 * 60 * 60))
  const days = Math.floor(diff / (1000 * 60 * 60 * 24))

  if (minutes < 1) return '刚刚'
  if (minutes < 60) return `${minutes}分钟前`
  if (hours < 24) return `${hours}小时前`
  if (days < 7) return `${days}天前`

  return time.toLocaleDateString('zh-CN')
}

// 截断文本
const truncateText = (text: string, maxLength: number) => {
  if (text.length <= maxLength) return text
  return text.substring(0, maxLength) + '...'
}

// 解析标签
const parseTags = (tags: string) => {
  if (!tags) return []
  return tags.split(',').map(tag => tag.trim()).filter(tag => tag)
}

// 处理分享
const handleShare = () => {
  const url = `${window.location.origin}/question-square/posts/${props.post.id}`

  if (navigator.share) {
    navigator.share({
      title: props.post.title,
      text: props.post.description || props.post.title,
      url: url
    }).catch(console.error)
  } else {
    // 降级到复制链接
    navigator.clipboard.writeText(url).then(() => {
      // 这里可以添加复制成功的提示
      console.log('链接已复制到剪贴板')
    }).catch(console.error)
  }
}
</script>

