  <div v-if="modelValue" class="dialog-overlay" @click="closeDialog">
    <div class="dialog-content" @click.stop>
      <div class="dialog-header">
        <h2>分享问题</h2>
        <button class="close-btn" @click="closeDialog">
          <i class="fas fa-times"></i>
        </button>
      </div>

      <form @submit.prevent="handleSubmit" class="dialog-body">
        <!-- 标题 -->
        <div class="form-group">
          <label for="title" class="form-label">
            标题 <span class="required">*</span>
          </label>
          <input
            id="title"
            v-model="form.title"
            type="text"
            class="form-input"
            placeholder="为你的问题起一个吸引人的标题"
            required
            maxlength="100"
          />
          <div class="char-count">{{ form.title.length }}/100</div>
        </div>

        <!-- 选择对话 -->
        <div class="form-group">
          <label for="conversation" class="form-label">
            选择对话 <span class="required">*</span>
          </label>
          <div class="conversation-selector">
            <select
              id="conversation"
              v-model="form.conversation_id"
              class="form-select"
              required
              @change="updateConversationInfo"
            >
              <option value="">请选择要分享的对话</option>
              <option 
                v-for="conv in conversations" 
                :key="conv.conversation_id"
                :value="conv.conversation_id"
              >
                {{ conv.prompt }}
              </option>
            </select>

            <button 
              type="button" 
              class="refresh-btn"
              @click="loadConversations"
              :disabled="loadingConversations"
            >
              <i class="fas fa-sync-alt" :class="{ 'fa-spin': loadingConversations }"></i>
            </button>
          </div>

          <div v-if="loadingConversations" class="loading-text">
            加载对话列表...
          </div>

          <div v-if="!loadingConversations && conversations.length === 0" class="empty-text">
            暂无可分享的对话
          </div>
        </div>

        <!-- 描述 -->
        <div class="form-group">
          <label for="description" class="form-label">描述</label>
          <textarea
            id="description"
            v-model="form.description"
            class="form-textarea"
            placeholder="简单描述一下你的问题背景和期望得到的帮助..."
            rows="4"
            maxlength="500"
          ></textarea>
          <div class="char-count">{{ form.description.length }}/500</div>
        </div>

        <!-- 标签 -->
        <div class="form-group">
          <label for="tags" class="form-label">标签</label>
          <input
            id="tags"
            v-model="form.tags"
            type="text"
            class="form-input"
            placeholder="添加相关标签，用逗号分隔，如：AI, 机器学习, 数据分析"
            maxlength="200"
          />
          <div class="form-help">
            标签有助于其他用户发现你的问题
          </div>
        </div>

        <!-- 按钮 -->
        <div class="dialog-footer">
          <button 
            type="button" 
            class="btn btn-secondary"
            @click="closeDialog"
          >
            取消
          </button>
          <button 
            type="submit" 
            class="btn btn-primary"
            :disabled="submitting || !form.title || !form.conversation_id"
          >
            <i v-if="submitting" class="fas fa-spinner fa-spin"></i>
            {{ submitting ? '分享中...' : '分享问题' }}
          </button>
        </div>
      </form>
    </div>
  </div>
