<script setup lang="ts">
import { ref, watch, onMounted } from 'vue'
import { questionSquareApi, type UserConversation } from '@/api/questionSquare'
import { researchApi } from '@/api/chat'

interface Props {
  modelValue: boolean
}

const props = defineProps<Props>()
const emit = defineEmits<{
  'update:modelValue': [value: boolean]
  'success': []
}>()

// 表单数据
const form = ref({
  title: '',
  conversation_id: '',
  description: '',
  tags: ''
})

// 对话列表
const conversations = ref<UserConversation[]>([])
const loadingConversations = ref(false)
const submitting = ref(false)

// 重置表单
const resetForm = () => {
  form.value = {
    title: '',
    conversation_id: '',
    description: '',
    tags: ''
  }
}

// 关闭弹窗
const closeDialog = () => {
  emit('update:modelValue', false)
  resetForm()
}

// 加载对话列表
const loadConversations = async () => {
  try {
    loadingConversations.value = true

    // 使用现有的 researchApi 获取用户对话
    const response = await researchApi.getMyChats(0, 50)

    conversations.value = response.map((chat: any) => ({
      conversation_id: chat.conversation_id,
      prompt: chat.prompt.length > 80 ? chat.prompt.substring(0, 80) + '...' : chat.prompt,
      created_at: chat.created_at
    }))
  } catch (error) {
    console.error('加载对话列表失败:', error)
    conversations.value = []
  } finally {
    loadingConversations.value = false
  }
}

// 更新对话信息
const updateConversationInfo = () => {
  const selectedConv = conversations.value.find(
    conv => conv.conversation_id === form.value.conversation_id
  )

  if (selectedConv && !form.value.title) {
    // 如果标题为空，可以基于对话内容生成建议标题
    const prompt = selectedConv.prompt.replace(/\.\.\.$/, '')
    if (prompt.length <= 50) {
      form.value.title = prompt
    } else {
      form.value.title = prompt.substring(0, 47) + '...'
    }
  }
}

// 提交表单
const handleSubmit = async () => {
  try {
    submitting.value = true

    await questionSquareApi.createPost({
      title: form.value.title,
      conversation_id: form.value.conversation_id,
      description: form.value.description || undefined,
      tags: form.value.tags || undefined
    })

    emit('success')
    closeDialog()
  } catch (error) {
    console.error('分享失败:', error)
    // 这里可以添加错误提示
  } finally {
    submitting.value = false
  }
}

// 监听弹窗打开
watch(() => props.modelValue, (newValue) => {
  if (newValue) {
    loadConversations()
  }
})

// 组件挂载时加载对话列表
onMounted(() => {
  if (props.modelValue) {
    loadConversations()
  }
})
</script>

