class CausalStreamingClient:
    def __init__(self, server_url='http://localhost:9092'):
        self.sio = socketio.Client()
        self.server_url = server_url
        self.session_id = None
        self.received_chunks = 0

        # Setup event handlers
        self.setup_handlers()

    def setup_handlers(self):
        @self.sio.event
        def connect():
            print("Connected to server")

        @self.sio.event
        def disconnect():
            print("Disconnected from server")

        @self.sio.event
        def connected(data):
            print(f"Server response: {data}")

        @self.sio.event
        def generation_started(data):
            print(f"Generation started: {data}")
            self.session_id = data.get('session_id')
            if self.session_id:
                self.sio.emit('join_session', {'session_id': self.session_id})

        @self.sio.event
        def session_joined(data):
            print(f"Joined session: {data}")

        @self.sio.event
        def prompt_encoding_complete(data):
            print(f"Prompt encoding complete: {data}")

        @self.sio.event
        def video_chunk(data):
            self.received_chunks += 1
            print(f"Received video chunk {self.received_chunks}: block {data.get('block_number', 'unknown')}, progress: {data.get('progress', 0):.1f}%")

        @self.sio.event
        def video_frame(data):
            print(f"Received frame {data.get('frame_number', 'unknown')}: block {data.get('block_number', 'unknown')}")

        @self.sio.event
        def block_complete(data):
            print(f"Block {data.get('block_number', 'unknown')} complete: {data.get('frames_in_block', 'unknown')} frames")

        @self.sio.event
        def chunk_complete(data):
            print(f"Chunk {data.get('chunk_number', 'unknown')} complete")

        @self.sio.event
        def generation_complete(data):
            print(f"Generation complete: {data}")

        @self.sio.event
        def generation_error(data):
            print(f"Generation error: {data}")

        @self.sio.event
        def error(data):
            print(f"Error: {data}")

    def connect(self):
        """Connect to the server"""
        try:
            self.sio.connect(self.server_url)
            return True
        except Exception as e:
            print(f"Failed to connect: {e}")
            return False

    def disconnect(self):
        """Disconnect from the server"""
        self.sio.disconnect()

    def encode_file_to_base64(self, file_path):
        """Encode a file to base64"""
        try:
            with open(file_path, 'rb') as f:
                return base64.b64encode(f.read()).decode('utf-8')
        except Exception as e:
            print(f"Failed to encode file {file_path}: {e}")
            return None

    def test_causal_generation(self, prompt, image_path=None, audio_path=None, 
                             num_blocks=3, frames_per_block=3):
        """Test causal video generation"""
        if not self.sio.connected:
            print("Not connected to server")
            return

        # Prepare data
        data = {
            'prompt': prompt,
            'num_blocks': num_blocks,
            'frames_per_block': frames_per_block,
            'height': 720,
            'width': 720,
            'num_steps': 4,
            'guidance_scale': 4.5,
            'negative_prompt': ''
        }

        # Add image if provided
        if image_path:
            image_base64 = self.encode_file_to_base64(image_path)
            if image_base64:
                data['image_base64'] = image_base64
                print(f"Added image: {image_path}")

        # Add audio if provided
        if audio_path:
            audio_base64 = self.encode_file_to_base64(audio_path)
            if audio_base64:
                data['audio_base64'] = audio_base64
                print(f"Added audio: {audio_path}")

        print(f"Starting causal generation with {num_blocks} blocks, {frames_per_block} frames per block")
        print(f"Prompt: {prompt}")

        # Send generation request
        self.sio.emit('generate_causal_streaming_base64', data)

        # Wait for completion
        print("Waiting for generation to complete...")
        return True

def main():
    # Test parameters
    prompt = "A realistic video of a man speaking directly to the camera on a sofa, with dynamic and rhythmic hand gestures that complement his speech."
    image_path = "/data2/jdsu/projects/OmniAvatar/examples/images/0000.jpeg"
