---
import Layout from "../../layouts/Layout.astro";
import { getCollection, getEntry } from 'astro:content';

export async function getStaticPaths() {
  const tasks = await getCollection('bench');
  return tasks.map((task) => ({
    params: { slug: task.slug },
    props: { task },
  }));
}

const { task } = Astro.props;
const { Content } = await task.render();

const taskNumber = parseInt(task.slug.replace('task_', ''));
const title = `Task ${taskNumber} - InnovatorBench`;
const description = `Detailed information about Task ${taskNumber} from the InnovatorBench benchmark suite.`;

// Get task metadata
const research_tags = task.data.research_tags || [];
const modality_tags = task.data.modality_tags || [];
const application_tags = task.data.application_tags || [];
const creator = task.data.creator || 'admin';
---

<Layout {title} {description}>
  <div class="task-detail">
    <div class="task-detail__breadcrumb">
      <a href="/tasks" class="breadcrumb-link">← Back to Tasks</a>
    </div>

    <div class="task-detail__content">
      <!-- Task Title -->
      <h1>Task {taskNumber}</h1>

      <!-- Tags Section -->
      {(research_tags.length > 0 || modality_tags.length > 0 || application_tags.length > 0) && (
        <div class="task-detail__tags">
          {research_tags && research_tags.length > 0 && research_tags.map((tag) => (
            <span class="task-tag task-tag--research">{tag}</span>
          ))}
          {modality_tags && modality_tags.length > 0 && modality_tags.map((tag) => (
            <span class="task-tag task-tag--modality">{tag}</span>
          ))}
          {application_tags && application_tags.length > 0 && application_tags.map((tag) => (
            <span class="task-tag task-tag--application">{tag}</span>
          ))}
        </div>
      )}

      <!-- Creator -->
      <p><strong>Created by:</strong> {creator}</p>

      <!-- Separator -->
      <hr />

      <!-- Task Content -->
      <Content />
    </div>
  </div>

  <style>
    .task-detail {
      max-width: 900px;
      margin: 0 auto;
      padding: 2rem 1rem;
    }

    .task-detail__breadcrumb {
      margin-bottom: 2rem;
    }

    .breadcrumb-link {
      color: var(--text-mid);
      text-decoration: none;
      font-size: 0.875rem;
      transition: color 0.2s ease;
    }

    .breadcrumb-link:hover {
      color: var(--primary);
    }

    .task-detail__content {
      line-height: 1.7;
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    }

    /* Tags styling */
    .task-detail__tags {
      display: flex;
      gap: 0.5rem;
      flex-wrap: wrap;
      margin: 1rem 0 1.5rem 0;
    }

    .task-tag {
      padding: 0.25rem 0.5rem;
      border-radius: 12px;
      font-size: 0.75rem;
      font-weight: 500;
    }

    .task-tag--research {
      background: rgba(102, 184, 255, 0.15);
      color: var(--primary);
    }

    .task-tag--modality {
      background: rgba(255, 165, 0, 0.15);
      color: #ff8c00;
    }

    .task-tag--application {
      background: rgba(34, 197, 94, 0.15);
      color: #22c55e;
    }

    /* Separator */
    hr {
      border: none;
      border-top: 1px solid var(--glass-border);
      margin: 2rem 0;
    }

    /* Content styling - clean markdown look */
    .task-detail__content :global(h1) {
      color: var(--text-high);
      font-size: 2.5rem;
      margin: 0 0 1rem 0;
      font-weight: 700;
    }

    .task-detail__content :global(h2) {
      color: var(--text-high);
      font-size: 1.875rem;
      margin: 2rem 0 1rem 0;
      font-weight: 600;
    }

    .task-detail__content :global(h3) {
      color: var(--text-high);
      font-size: 1.5rem;
      margin: 1.5rem 0 0.75rem 0;
      font-weight: 600;
    }

    .task-detail__content :global(h4) {
      color: var(--text-high);
      font-size: 1.25rem;
      margin: 1.25rem 0 0.5rem 0;
      font-weight: 600;
    }

    .task-detail__content :global(h5) {
      color: var(--text-high);
      font-size: 1.125rem;
      margin: 1rem 0 0.5rem 0;
      font-weight: 600;
    }

    .task-detail__content :global(h6) {
      color: var(--text-high);
      font-size: 1rem;
      margin: 1rem 0 0.5rem 0;
      font-weight: 600;
    }

    .task-detail__content :global(p) {
      color: var(--text-mid);
      margin-bottom: 1rem;
    }

    .task-detail__content :global(ul), 
    .task-detail__content :global(ol) {
      color: var(--text-mid);
      padding-left: 1.5rem;
      margin-bottom: 1rem;
    }

    .task-detail__content :global(li) {
      margin-bottom: 0.5rem;
    }

    .task-detail__content :global(strong) {
      color: var(--text-high);
      font-weight: 600;
    }

    .task-detail__content :global(em) {
      font-style: italic;
    }

    .task-detail__content :global(code) {
      background: rgba(4, 8, 16, 0.6);
      border: 1px solid var(--glass-border);
      border-radius: 4px;
      padding: 0.25rem 0.5rem;
      font-family: 'SF Mono', 'Monaco', 'Inconsolata', 'Roboto Mono', monospace;
      font-size: 0.875rem;
      color: var(--primary);
