      border-radius: 8px;
      padding: 1rem;
      overflow-x: auto;
      margin: 1rem 0;
    }

    .task-detail__content :global(pre code) {
      background: none;
      border: none;
      padding: 0;
    }

    .task-detail__content :global(blockquote) {
      border-left: 4px solid var(--primary);
      background: rgba(102, 184, 255, 0.05);
      padding: 1rem 1.5rem;
      margin: 1rem 0;
      border-radius: 0 8px 8px 0;
    }

    .task-detail__content :global(blockquote p) {
      margin: 0;
    }

    .task-detail__content :global(table) {
      width: 100%;
      border-collapse: collapse;
      margin: 1rem 0;
      background: rgba(4, 8, 16, 0.3);
      border-radius: 8px;
      overflow: hidden;
    }

    .task-detail__content :global(th),
    .task-detail__content :global(td) {
      padding: 0.75rem;
      text-align: left;
      border-bottom: 1px solid var(--glass-border);
    }

    .task-detail__content :global(th) {
      background: rgba(4, 8, 16, 0.6);
      color: var(--text-high);
      font-weight: 600;
    }

    .task-detail__content :global(td) {
      color: var(--text-mid);
    }

    .task-detail__content :global(a) {
      color: var(--primary);
      text-decoration: none;
      transition: color 0.2s ease;
    }

    .task-detail__content :global(a:hover) {
      color: #7dd3fc;
      text-decoration: underline;
    }

    @media (max-width: 768px) {
      .task-detail {
        padding: 1rem;
      }

      .task-detail__content :global(h1) {
        font-size: 2rem;
      }

      .task-detail__content :global(h2) {
        font-size: 1.5rem;
      }

      .task-detail__content :global(pre) {
        padding: 0.75rem;
        font-size: 0.875rem;
      }

      .task-detail__content :global(table) {
        font-size: 0.875rem;
      }

      .task-detail__content :global(th),
      .task-detail__content :global(td) {
        padding: 0.5rem;
      }
    }
  </style>


</Layout>

