interface CommandProps {
  children: React.ReactNode;
  className?: string;
}

interface CommandInputProps {
  placeholder?: string;
  value?: string;
  onValueChange?: (value: string) => void;
  className?: string;
}

interface CommandListProps {
  children: React.ReactNode;
  className?: string;
}

interface CommandEmptyProps {
  children: React.ReactNode;
  className?: string;
}

interface CommandGroupProps {
  children: React.ReactNode;
  heading?: string;
  className?: string;
}

interface CommandItemProps {
  children: React.ReactNode;
  onSelect?: () => void;
  className?: string;
}

const Command = ({ children, className = '' }: CommandProps) => {
  return (
    <div className={`command ${className}`}>
      {children}
    </div>
  );
};

const CommandInput = ({ 
  placeholder, 
  value, 
  onValueChange, 
  className = '' 
}: CommandInputProps) => {
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
        e.preventDefault();
        inputRef.current?.focus();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, []);

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Escape') {
      onValueChange?.('');
      inputRef.current?.blur();
    }
  };

  return (
    <div className="command-input-wrapper">
      <div className="command-input-icon">
        <svg
          width="16"
          height="16"
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          strokeLinecap="round"
          strokeLinejoin="round"
        >
          <circle cx="11" cy="11" r="8" />
          <path d="m21 21-4.35-4.35" />
        </svg>
      </div>
      <input
        ref={inputRef}
        type="text"
        className={`command-input ${className}`}
        placeholder={placeholder}
        value={value}
        onChange={(e) => onValueChange?.(e.target.value)}
        onKeyDown={handleKeyDown}
      />
      <div className="command-shortcut">
        <kbd className="command-kbd">⌘</kbd>
        <kbd className="command-kbd">K</kbd>
      </div>
    </div>
  );
};

const CommandList = ({ children, className = '' }: CommandListProps) => {
  return (
    <div className={`command-list ${className}`}>
      {children}
    </div>
  );
};

const CommandEmpty = ({ children, className = '' }: CommandEmptyProps) => {
  return (
    <div className={`command-empty ${className}`}>
      {children}
    </div>
  );
};

const CommandGroup = ({ children, heading, className = '' }: CommandGroupProps) => {
  return (
    <div className={`command-group ${className}`}>
      {heading && <div className="command-group-heading">{heading}</div>}
      {children}
    </div>
  );
};

const CommandItem = ({ children, onSelect, className = '' }: CommandItemProps) => {
  return (
    <div 
      className={`command-item ${className}`}
      onClick={onSelect}
    >
      {children}
    </div>
  );
};

export {
  Command,
  CommandInput,
  CommandList,
  CommandEmpty,
  CommandGroup,
  CommandItem,
};"}

