	// Text rotation animation with magical effects
	const rotatingText = document.getElementById('rotating-text');
	const texts = [
		'LLM research',
		'Data Construction', 
		'Data Filtering',
		'Data Augmentation',
		'Reward Design',
		'Loss Design',
		'Scaffold Construction'
	];

	let currentIndex = 0;

	function createSparkles() {
		const sparkleContainer = document.createElement('div');
		sparkleContainer.className = 'sparkle-container';
		rotatingText.appendChild(sparkleContainer);

		for (let i = 0; i < 6; i++) {
			const sparkle = document.createElement('div');
			sparkle.className = 'sparkle';
			sparkle.style.left = Math.random() * 100 + '%';
			sparkle.style.top = Math.random() * 100 + '%';
			sparkle.style.animationDelay = Math.random() * 0.5 + 's';
			sparkleContainer.appendChild(sparkle);
		}

		setTimeout(() => {
			sparkleContainer.remove();
		}, 1000);
	}

	function animateTextChange() {
		// Create sparkles effect
		createSparkles();

		// Magical disappear effect
		rotatingText.style.opacity = '0';
		rotatingText.style.transform = 'scale(0.3) rotateY(180deg)';
		rotatingText.style.filter = 'blur(10px)';

		setTimeout(() => {
			// Change text
			currentIndex = (currentIndex + 1) % texts.length;
			rotatingText.textContent = texts[currentIndex];

			// Magical appear effect
			rotatingText.style.opacity = '1';
			rotatingText.style.transform = 'scale(1) rotateY(0deg)';
			rotatingText.style.filter = 'blur(0px)';
		}, 400);
	}

	// Start the animation after page load
	setTimeout(() => {
		setInterval(animateTextChange, 3500);
	}, 2000);
</script>

