{"file_path":"/Users/yangxiao/projects/vscode/Wechat-Moment-Resources/wechat-clone/backend/src/index.ts","content":"import express from 'express';
import { createServer } from 'http';
import { Server } from 'socket.io';
import cors from 'cors';
import helmet from 'helmet';
import compression from 'compression';
import morgan from 'morgan';
import dotenv from 'dotenv';

import { connectMongoDB } from './config/mongodb';
import { connectRedis } from './config/redis';
import { logger } from './utils/logger';
import { errorHandler } from './middlewares/errorHandler';
import { setupSocketIO } from './services/socket/socketHandler';

// è·¯ç”±
import authRoutes from './routes/auth';
import userRoutes from './routes/user';
import friendRoutes from './routes/friend';
import messageRoutes from './routes/message';
import momentRoutes from './routes/moment';
import uploadRoutes from './routes/upload';

// åŠ è½½ç¯å¢ƒå˜é‡
dotenv.config();

const app = express();
const httpServer = createServer(app);
const io = new Server(httpServer, {
  cors: {
    origin: process.env.CORS_ORIGIN || '*',
    methods: ['GET', 'POST'],
  },
});

const PORT = process.env.PORT || 3000;
const HOST = process.env.HOST || '0.0.0.0';

// ä¸­é—´ä»¶
app.use(helmet());
app.use(cors());
app.use(compression());
app.use(express.json());
app.use(express.urlencoded({ extended: true }));
app.use(morgan('combined', { stream: { write: (message) => logger.info(message.trim()) } }));

// å¥åº·æ£€æŸ¥
app.get('/health', (req, res) => {
  res.json({
    status: 'ok',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
  });
});

// API è·¯ç”±
app.use('/api/auth', authRoutes);
app.use('/api/users', userRoutes);
app.use('/api/friends', friendRoutes);
app.use('/api/messages', messageRoutes);
app.use('/api/moments', momentRoutes);
app.use('/api/upload', uploadRoutes);

// 404 å¤„ç†
app.use((req, res) => {
  res.status(404).json({
    success: false,
    message: 'Route not found',
  });
});

// é”™è¯¯å¤„ç†
app.use(errorHandler);

// Socket.IO è®¾ç½®
setupSocketIO(io);

// å¯åŠ¨æœåŠ¡å™¨
async function startServer() {
  try {
    // è¿æ¥æ•°æ®åº“
    await connectMongoDB();
    await connectRedis();

    // å¯åŠ¨ HTTP æœåŠ¡å™¨
    httpServer.listen(PORT, HOST, () => {
      logger.info(`ğŸš€ Server is running on http://${HOST}:${PORT}`);
      logger.info(`ğŸ“¡ Socket.IO is ready`);
      logger.info(`ğŸŒ Environment: ${process.env.NODE_ENV || 'development'}`);
    });
  } catch (error) {
    logger.error('Failed to start server:', error);
    process.exit(1);
  }
}

// ä¼˜é›…å…³é—­
process.on('SIGTERM', () => {
  logger.info('SIGTERM signal received: closing HTTP server');
  httpServer.close(() => {
    logger.info('HTTP server closed');
    process.exit(0);
  });
});

process.on('SIGINT', () => {
  logger.info('SIGINT signal received: closing HTTP server');
  httpServer.close(() => {
    logger.info('HTTP server closed');
    process.exit(0);
  });
});

startServer();

export { app, io };
"}

{"file_path":"/Users/yangxiao/projects/vscode/Wechat-Moment-Resources/wechat-clone/backend/src/config/mongodb.ts","content":"import mongoose from 'mongoose';
import { logger } from '../utils/logger';

const MONGODB_URI = process.env.MONGODB_URI || 'mongodb://admin:admin123@localhost:27017/wechat?authSource=admin';

export async function connectMongoDB(): Promise<void> {
  try {
    await mongoose.connect(MONGODB_URI);
    logger.info('âœ… MongoDB connected successfully');

    mongoose.connection.on('error', (error) => {
      logger.error('MongoDB connection error:', error);
    });

    mongoose.connection.on('disconnected', () => {
      logger.warn('MongoDB disconnected');
    });

    mongoose.connection.on('reconnected', () => {
      logger.info('MongoDB reconnected');
    });
  } catch (error) {
    logger.error('Failed to connect to MongoDB:', error);
    throw error;
  }
}

export async function disconnectMongoDB(): Promise<void> {
  try {
    await mongoose.disconnect();
    logger.info('MongoDB disconnected');
  } catch (error) {
    logger.error('Error disconnecting from MongoDB:', error);
    throw error;
  }
}
"}

{"file_path":"/Users/yangxiao/projects/vscode/Wechat-Moment-Resources/wechat-clone/backend/src/config/redis.ts","content":"import { createClient, RedisClientType } from 'redis';
import { logger } from '../utils/logger';

let redisClient: RedisClientType;

export async function connectRedis(): Promise<void> {
  try {
    redisClient = createClient({
      socket: {
        host: process.env.REDIS_HOST || 'localhost',
        port: parseInt(process.env.REDIS_PORT || '6379'),
      },
      password: process.env.REDIS_PASSWORD || 'redis123',
    });

    redisClient.on('error', (error) => {
      logger.error('Redis Client Error:', error);
    });

    redisClient.on('connect', () => {
      logger.info('âœ… Redis connected successfully');
    });

    redisClient.on('reconnecting', () => {
      logger.warn('Redis reconnecting...');
    });

    redisClient.on('ready', () => {
      logger.info('Redis client ready');
    });

    await redisClient.connect();
  } catch (error) {
    logger.error('Failed to connect to Redis:', error);
    throw error;
  }
}

export function getRedisClient(): RedisClientType {
  if (!redisClient) {
    throw new Error('Redis client not initialized');
  }
  return redisClient;
}

export async function disconnectRedis(): Promise<void> {
  try {
    if (redisClient) {
      await redisClient.quit();
      logger.info('Redis disconnected');
    }
  } catch (error) {
    logger.error('Error disconnecting from Redis:', error);
    throw error;
  }
}
"}

{"file_path":"/Users/yangxiao/projects/vscode/Wechat-Moment-Resources/wechat-clone/backend/src/utils/logger.ts","content":"import winston from 'winston';
import path from 'path';

const logLevel = process.env.LOG_LEVEL || 'info';
const logFile = process.env.LOG_FILE || 'logs/app.log';

// è‡ªå®šä¹‰æ—¥å¿—æ ¼å¼
const logFormat = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
  winston.format.errors({ stack: true }),
  winston.format.splat(),
  winston.format.json()
);

// æ§åˆ¶å°è¾“å‡ºæ ¼å¼
const consoleFormat = winston.format.combine(
  winston.format.colorize(),
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
  winston.format.printf(({ timestamp, level, message, ...meta }) => {
    let msg = `${timestamp} [${level}]: ${message}`;
    if (Object.keys(meta).length > 0) {
      msg += ` ${JSON.stringify(meta)}`;
    }
    return msg;
  })
);

// åˆ›å»º logger
export const logger = winston.createLogger({
  level: logLevel,
  format: logFormat,
  transports: [
    // æ§åˆ¶å°è¾“å‡º
    new winston.transports.Console({
      format: consoleFormat,
    }),
    // æ–‡ä»¶è¾“å‡º
    new winston.transports.File({
      filename: path.join(process.cwd(), logFile),
      maxsize: 5242880, // 5MB
      maxFiles: 5,
    }),
    // é”™è¯¯æ—¥å¿—å•ç‹¬æ–‡ä»¶
    new winston.transports.File({
      filename: path.join(process.cwd(), 'logs/error.log'),
      level: 'error',
      maxsize: 5242880,
      maxFiles: 5,
    }),
  ],
});

// å¼€å‘ç¯å¢ƒä¸‹è¾“å‡ºæ›´è¯¦ç»†çš„æ—¥å¿—
if (process.env.NODE_ENV === 'development') {
  logger.level = 'debug';
}
"}

{"file_path":"/Users/yangxiao/projects/vscode/Wechat-Moment-Resources/wechat-clone/backend/src/models/User.ts","content":"import mongoose, { Document, Schema } from 'mongoose';
import bcrypt from 'bcryptjs';

export interface IUser extends Document {
  username: string;
  email: string;
  phone?: string;
  password: string;
  avatar?: string;
  nickname: string;
  bio?: string;
  gender?: 'male' | 'female' | 'other';
  birthday?: Date;
  location?: string;
  isOnline: boolean;
  lastSeen: Date;
  createdAt: Date;
  updatedAt: Date;
  comparePassword(candidatePassword: string): Promise<boolean>;
}

const userSchema = new Schema<IUser>(
  {
    username: {
      type: String,
      required: true,
      unique: true,
      trim: true,
      minlength: 3,
      maxlength: 30,
    },
    email: {
      type: String,
      required: true,
      unique: true,
      trim: true,
      lowercase: true,
    },
    phone: {
      type: String,
      unique: true,
      sparse: true,
      trim: true,
    },
    password: {
      type: String,
      required: true,
      minlength: 6,
    },
    avatar: {
      type: String,
      default: '',
    },
    nickname: {
      type: String,
      required: true,
      trim: true,
      maxlength: 50,
    },
    bio: {
      type: String,
      maxlength: 200,
      default: '',
    },
    gender: {
      type: String,
      enum: ['male', 'female', 'other'],
    },
    birthday: {
      type: Date,
    },
    location: {
      type: String,
      maxlength: 100,
    },
    isOnline: {
      type: Boolean,
      default: false,
    },
    lastSeen: {
      type: Date,
      default: Date.now,
    },
  },
  {
    timestamps: true,
  }
);

// å¯†ç åŠ å¯†ä¸­é—´ä»¶
userSchema.pre('save', async function (next) {
  if (!this.isModified('password')) {
    return next();
  }

  try {
    const salt = await bcrypt.genSalt(10);
    this.password = await bcrypt.hash(this.password, salt);
    next();
  } catch (error: any) {
    next(error);
  }
});

// å¯†ç æ¯”å¯¹æ–¹æ³•
userSchema.methods.comparePassword = async function (candidatePassword: string): Promise<boolean> {
  return bcrypt.compare(candidatePassword, this.password);
};

// è½¬æ¢ä¸º JSON æ—¶ç§»é™¤æ•æ„Ÿä¿¡æ¯
userSchema.methods.toJSON = function () {
  const obj = this.toObject();
  delete obj.password;
  return obj;
};

export const User = mongoose.model<IUser>('User', userSchema);
"}

