    if (!isPasswordValid) {
      res.status(401).json({
        success: false,
        message: 'Invalid credentials',
      });
      return;
    }

    // ç”Ÿæˆ token
    const accessToken = generateAccessToken({
      userId: user._id.toString(),
      username: user.username,
