import { Friendship, FriendRequest } from '../models/Friendship';
import { User } from '../models/User';
import { authenticate } from '../middlewares/auth';
import { logger } from '../utils/logger';

const router = Router();

// è·å–å¥½å‹åˆ—è¡¨
router.get('/', authenticate, async (req: Request, res: Response) => {
  try {
    const userId = req.user?.userId;

    const friendships = await Friendship.find({ userId, isBlocked: false });
    const friendIds = friendships.map((f) => f.friendId);

    const friends = await User.find({ _id: { $in: friendIds } }).select('-password');

    // åˆå¹¶å¥½å‹ä¿¡æ¯å’Œå¤‡æ³¨
    const friendsWithAlias = friends.map((friend) => {
      const friendship = friendships.find((f) => f.friendId === friend._id.toString());
      return {
        ...friend.toJSON(),
        alias: friendship?.alias,
        tags: friendship?.tags,
        isFavorite: friendship?.isFavorite,
      };
    });

    res.json({
      success: true,
      data: friendsWithAlias,
    });
  } catch (error: any) {
    logger.error('Get friends error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

// å‘é€å¥½å‹ç”³è¯·
router.post('/request', authenticate, async (req: Request, res: Response) => {
  try {
    const fromUserId = req.user?.userId!;
    const { toUserId, message } = req.body;

    if (!toUserId) {
      res.status(400).json({
        success: false,
        message: 'toUserId is required',
      });
      return;
    }

    // æ£€æŸ¥ç›®æ ‡ç”¨æˆ·æ˜¯å¦å­˜åœ¨
    const toUser = await User.findById(toUserId);
    if (!toUser) {
      res.status(404).json({
        success: false,
        message: 'User not found',
      });
      return;
    }

    // æ£€æŸ¥æ˜¯å¦å·²ç»æ˜¯å¥½å‹
    const existingFriendship = await Friendship.findOne({
      userId: fromUserId,
      friendId: toUserId,
    });

    if (existingFriendship) {
      res.status(400).json({
        success: false,
        message: 'Already friends',
      });
      return;
    }

    // æ£€æŸ¥æ˜¯å¦å·²ç»å‘é€è¿‡ç”³è¯·
    const existingRequest = await FriendRequest.findOne({
      fromUserId,
      toUserId,
      status: 'pending',
    });

    if (existingRequest) {
      res.status(400).json({
        success: false,
        message: 'Friend request already sent',
      });
      return;
    }

    // åˆ›å»ºå¥½å‹ç”³è¯·
    const friendRequest = new FriendRequest({
      fromUserId,
      toUserId,
      message,
    });

    await friendRequest.save();

    logger.info(`Friend request sent from ${fromUserId} to ${toUserId}`);

    res.status(201).json({
      success: true,
      message: 'Friend request sent',
      data: friendRequest,
    });
  } catch (error: any) {
    logger.error('Send friend request error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

// è·å–å¥½å‹ç”³è¯·åˆ—è¡¨
router.get('/requests', authenticate, async (req: Request, res: Response) => {
  try {
    const userId = req.user?.userId;

    const requests = await FriendRequest.find({
      toUserId: userId,
      status: 'pending',
    }).sort({ createdAt: -1 });

    const fromUserIds = requests.map((r) => r.fromUserId);
    const users = await User.find({ _id: { $in: fromUserIds } }).select('-password');

    const requestsWithUser = requests.map((request) => {
      const user = users.find((u) => u._id.toString() === request.fromUserId);
      return {
        ...request.toJSON(),
        fromUser: user,
      };
    });

    res.json({
      success: true,
      data: requestsWithUser,
    });
  } catch (error: any) {
    logger.error('Get friend requests error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

// æ¥å—å¥½å‹ç”³è¯·
router.post('/requests/:requestId/accept', authenticate, async (req: Request, res: Response) => {
  try {
    const { requestId } = req.params;
    const userId = req.user?.userId!;

    const request = await FriendRequest.findById(requestId);

    if (!request) {
      res.status(404).json({
        success: false,
        message: 'Friend request not found',
      });
      return;
    }

    if (request.toUserId !== userId) {
      res.status(403).json({
        success: false,
        message: 'Unauthorized',
      });
      return;
    }

    if (request.status !== 'pending') {
      res.status(400).json({
        success: false,
        message: 'Request already processed',
      });
      return;
    }

    // æ›´æ–°ç”³è¯·çŠ¶æ€
    request.status = 'accepted';
    await request.save();

    // åˆ›å»ºåŒå‘å¥½å‹å…³ç³»
    await Friendship.create([
      { userId: request.fromUserId, friendId: request.toUserId },
      { userId: request.toUserId, friendId: request.fromUserId },
    ]);

    logger.info(`Friend request accepted: ${requestId}`);

    res.json({
      success: true,
      message: 'Friend request accepted',
    });
  } catch (error: any) {
    logger.error('Accept friend request error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

// æ‹’ç»å¥½å‹ç”³è¯·
router.post('/requests/:requestId/reject', authenticate, async (req: Request, res: Response) => {
  try {
    const { requestId } = req.params;
    const userId = req.user?.userId!;

    const request = await FriendRequest.findById(requestId);

    if (!request) {
      res.status(404).json({
        success: false,
        message: 'Friend request not found',
      });
      return;
    }

    if (request.toUserId !== userId) {
      res.status(403).json({
        success: false,
        message: 'Unauthorized',
      });
      return;
    }

    request.status = 'rejected';
    await request.save();

    logger.info(`Friend request rejected: ${requestId}`);

    res.json({
      success: true,
      message: 'Friend request rejected',
    });
  } catch (error: any) {
    logger.error('Reject friend request error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

// åˆ é™¤å¥½å‹
router.delete('/:friendId', authenticate, async (req: Request, res: Response) => {
  try {
    const userId = req.user?.userId!;
    const { friendId } = req.params;

    // åˆ é™¤åŒå‘å¥½å‹å…³ç³»
    await Friendship.deleteMany({
      $or: [
        { userId, friendId },
        { userId: friendId, friendId: userId },
      ],
    });

    logger.info(`Friendship deleted: ${userId} - ${friendId}`);

    res.json({
      success: true,
      message: 'Friend deleted',
    });
  } catch (error: any) {
    logger.error('Delete friend error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

export default router;
"}

