        friendId: friendRequest.toUserId,
      },
      {
        userId: friendRequest.toUserId,
        friendId: friendRequest.fromUserId,
      },
    ]);

    logger.info(`Friend request accepted: ${requestId}`);

    res.json({
      success: true,
      message: 'Friend request accepted',
    });
