      res.status(403).json({
        success: false,
        message: 'Unauthorized',
      });
      return;
    }

    moment.isDeleted = true;
    await moment.save();

    logger.info(`Moment deleted: ${momentId}`);

    res.json({
      success: true,
      message: 'Moment deleted',
    });
  } catch (error: any) {
    logger.error('Delete moment error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

