      res.status(400).json({
        success: false,
        message: 'No files uploaded',
      });
      return;
    }

    const userId = req.user?.userId;
    const files = req.files;

    const uploadPromises = files.map(async (file) => {
      let bucketType: 'images' | 'videos' | 'files' = 'files';
      if (file.mimetype.startsWith('image/')) {
        bucketType = 'images';
      } else if (file.mimetype.startsWith('video/')) {
        bucketType = 'videos';
      }

      const timestamp = Date.now();
      const extension = file.originalname.split('.').pop();
      const filename = `${userId}/${timestamp}-${Math.random().toString(36).substring(7)}.${extension}`;

      const url = await uploadToStorage(file.buffer, filename, bucketType, file.mimetype);

      return {
        url,
        filename,
        size: file.size,
        mimetype: file.mimetype,
      };
    });

    const results = await Promise.all(uploadPromises);

    logger.info(`Multiple files uploaded: ${results.length} files`);

    res.json({
      success: true,
      data: results,
    });
