import * as Minio from 'minio';
import { logger } from '../../utils/logger';

const STORAGE_TYPE = process.env.STORAGE_TYPE || 'minio';

// MinIO ÂÆ¢Êà∑Á´Ø
let minioClient: Minio.Client | null = null;

if (STORAGE_TYPE === 'minio') {
  minioClient = new Minio.Client({
    endPoint: process.env.MINIO_ENDPOINT || 'localhost',
    port: parseInt(process.env.MINIO_PORT || '9000'),
    useSSL: process.env.MINIO_USE_SSL === 'true',
    accessKey: process.env.MINIO_ACCESS_KEY || 'minioadmin',
    secretKey: process.env.MINIO_SECRET_KEY || 'minioadmin123',
  });
}

// Ëé∑ÂèñÂ≠òÂÇ®Ê°∂ÂêçÁß∞
function getBucketName(type: 'images' | 'videos' | 'files'): string {
  const buckets = {
    images: process.env.MINIO_BUCKET_IMAGES || 'wechat-images',
    videos: process.env.MINIO_BUCKET_VIDEOS || 'wechat-videos',
    files: process.env.MINIO_BUCKET_FILES || 'wechat-files',
  };
  return buckets[type];
}

/**
 * ‰∏ä‰º†Êñá‰ª∂Âà∞Â≠òÂÇ®ÊúçÂä°
 */
export async function uploadToStorage(
  buffer: Buffer,
  filename: string,
  bucketType: 'images' | 'videos' | 'files',
  contentType: string
): Promise<string> {
  if (STORAGE_TYPE === 'minio') {
    return uploadToMinIO(buffer, filename, bucketType, contentType);
  } else if (STORAGE_TYPE === 'oss') {
    // TODO: ÂÆûÁé∞ÈòøÈáå‰∫ë OSS ‰∏ä‰º†
    throw new Error('OSS upload not implemented yet');
  } else {
    throw new Error(`Unsupported storage type: ${STORAGE_TYPE}`);
  }
}

/**
 * ‰∏ä‰º†Âà∞ MinIO
 */
async function uploadToMinIO(
  buffer: Buffer,
  filename: string,
  bucketType: 'images' | 'videos' | 'files',
  contentType: string
): Promise<string> {
  if (!minioClient) {
    throw new Error('MinIO client not initialized');
  }

  const bucketName = getBucketName(bucketType);

  try {
    // Á°Æ‰øùÂ≠òÂÇ®Ê°∂Â≠òÂú®
    const bucketExists = await minioClient.bucketExists(bucketName);
    if (!bucketExists) {
      await minioClient.makeBucket(bucketName, 'us-east-1');
      logger.info(`Bucket created: ${bucketName}`);
    }

    // ‰∏ä‰º†Êñá‰ª∂
    await minioClient.putObject(bucketName, filename, buffer, buffer.length, {
      'Content-Type': contentType,
    });

    // ÁîüÊàêËÆøÈóÆ URL
    const endpoint = process.env.MINIO_ENDPOINT || 'localhost';
    const port = process.env.MINIO_PORT || '9000';
    const useSSL = process.env.MINIO_USE_SSL === 'true';
    const protocol = useSSL ? 'https' : 'http';

    const url = `${protocol}://${endpoint}:${port}/${bucketName}/${filename}`;

    logger.info(`File uploaded to MinIO: ${url}`);

    return url;
  } catch (error: any) {
    logger.error('MinIO upload error:', error);
    throw new Error(`Failed to upload file: ${error.message}`);
  }
}

/**
 * Âà†Èô§Êñá‰ª∂
 */
export async function deleteFromStorage(
  filename: string,
  bucketType: 'images' | 'videos' | 'files'
): Promise<void> {
  if (STORAGE_TYPE === 'minio') {
    return deleteFromMinIO(filename, bucketType);
  } else if (STORAGE_TYPE === 'oss') {
    // TODO: ÂÆûÁé∞ÈòøÈáå‰∫ë OSS Âà†Èô§
    throw new Error('OSS delete not implemented yet');
  } else {
    throw new Error(`Unsupported storage type: ${STORAGE_TYPE}`);
  }
}

/**
 * ‰ªé MinIO Âà†Èô§
 */
async function deleteFromMinIO(filename: string, bucketType: 'images' | 'videos' | 'files'): Promise<void> {
  if (!minioClient) {
    throw new Error('MinIO client not initialized');
  }

  const bucketName = getBucketName(bucketType);

  try {
    await minioClient.removeObject(bucketName, filename);
    logger.info(`File deleted from MinIO: ${filename}`);
  } catch (error: any) {
    logger.error('MinIO delete error:', error);
    throw new Error(`Failed to delete file: ${error.message}`);
  }
}


