router.post('/', authenticate, async (req: Request, res: Response) => {
  try {
    const userId = req.user?.userId;
    const { content, images, videos, location, visibility } = req.body;

    if (!content) {
      res.status(400).json({
        success: false,
        message: 'Content is required',
      });
      return;
    }

    const moment = new Moment({
      userId,
      content,
      images: images || [],
      videos: videos || [],
      location,
      visibility: visibility || 'friends',
    });

    await moment.save();

    logger.info(`Moment created: ${moment._id}`);

    res.status(201).json({
      success: true,
      data: moment,
    });
  } catch (error: any) {
    logger.error('Create moment error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

// Ëé∑ÂèñÊúãÂèãÂúàÂàóË°®ÔºàÂåÖÊã¨Ëá™Â∑±ÂíåÂ•ΩÂèãÁöÑÔºâ
router.get('/', authenticate, async (req: Request, res: Response) => {
  try {
    const userId = req.user?.userId;
    const { limit = 20, before } = req.query;

    // Ëé∑ÂèñÂ•ΩÂèãÂàóË°®
    const friendships = await Friendship.find({ userId });
    const friendIds = friendships.map((f) => f.friendId);
    const userIds = [userId, ...friendIds];

    const query: any = {
      userId: { $in: userIds },
      isDeleted: false,
      visibility: { $in: ['public', 'friends'] },
    };

    if (before) {
      query.createdAt = { $lt: new Date(before as string) };
    }

    const moments = await Moment.find(query)
      .sort({ createdAt: -1 })
      .limit(Number(limit));

    // Ëé∑ÂèñÂèëÂ∏ÉËÄÖ‰ø°ÊÅØ
    const userIdsInMoments = [...new Set(moments.map((m) => m.userId))];
    const users = await User.find({ _id: { $in: userIdsInMoments } }).select('-password');

    // Ëé∑ÂèñÁÇπËµûÂíåËØÑËÆ∫‰ø°ÊÅØ
    const momentIds = moments.map((m) => m._id.toString());
    const likes = await MomentLike.find({ momentId: { $in: momentIds } });
    const comments = await MomentComment.find({ momentId: { $in: momentIds }, isDeleted: false });

    const momentsWithDetails = moments.map((moment) => {
      const user = users.find((u) => u._id.toString() === moment.userId);
      const momentLikes = likes.filter((l) => l.momentId === moment._id.toString());
      const momentComments = comments.filter((c) => c.momentId === moment._id.toString());
      const isLiked = momentLikes.some((l) => l.userId === userId);

      return {
        ...moment.toJSON(),
        user,
        isLiked,
        likes: momentLikes,
        comments: momentComments,
      };
    });

    res.json({
      success: true,
      data: momentsWithDetails,
    });
  } catch (error: any) {
    logger.error('Get moments error:', error);
    res.status(500).json({
      success: false,
      message: error.message,
    });
  }
});

