import { Router, Request, Response } from 'express';
import multer from 'multer';
import { authenticate } from '../middlewares/auth';
import { uploadToStorage } from '../services/storage/storageService';
import { logger } from '../utils/logger';

const router = Router();

// ÈÖçÁΩÆ multer
const storage = multer.memoryStorage();
const upload = multer({
  storage,
  limits: {
    fileSize: parseInt(process.env.MAX_FILE_SIZE || '10485760'), // ÈªòËÆ§ 10MB
  },
  fileFilter: (req, file, cb) => {
    // ÂÖÅËÆ∏ÁöÑÊñá‰ª∂Á±ªÂûã
    const allowedMimes = [
      'image/jpeg',
      'image/png',
      'image/gif',
      'image/webp',
      'video/mp4',
      'video/quicktime',
      'audio/mpeg',
      'audio/wav',
      'application/pdf',
    ];

    if (allowedMimes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type'));
    }
  },
});

// ‰∏ä‰º†Âçï‰∏™Êñá‰ª∂
router.post('/single', authenticate, upload.single('file'), async (req: Request, res: Response) => {
  try {
    if (!req.file) {
      res.status(400).json({
        success: false,
        message: 'No file uploaded',
      });
      return;
    }

    const userId = req.user?.userId;
    const file = req.file;

    // Á°ÆÂÆöÊñá‰ª∂Á±ªÂûãÂíåÂ≠òÂÇ®Ê°∂
    let bucketType: 'images' | 'videos' | 'files' = 'files';
    if (file.mimetype.startsWith('image/')) {
      bucketType = 'images';
    } else if (file.mimetype.startsWith('video/')) {
      bucketType = 'videos';
    }

    // ÁîüÊàêÊñá‰ª∂Âêç
    const timestamp = Date.now();
    const extension = file.originalname.split('.').pop();
    const filename = `${userId}/${timestamp}.${extension}`;

    // ‰∏ä‰º†Âà∞Â≠òÂÇ®ÊúçÂä°
    const url = await uploadToStorage(file.buffer, filename, bucketType, file.mimetype);

    logger.info(`File uploaded: ${filename}`);

    res.json({
      success: true,
      data: {
        url,
        filename,
        size: file.size,
        mimetype: file.mimetype,
      },
    });
  } catch (error: any) {
