import { Server, Socket } from 'socket.io';
import { verifyAccessToken } from '../../utils/jwt';
import { getRedisClient } from '../../config/redis';
import { Message } from '../../models/Message';
import { User } from '../../models/User';
import { logger } from '../../utils/logger';

interface AuthenticatedSocket extends Socket {
  userId?: string;
  username?: string;
}

// Â≠òÂÇ®Áî®Êà∑ socket Êò†Â∞Ñ
const userSockets = new Map<string, string>(); // userId -> socketId

export function setupSocketIO(io: Server): void {
  // ËÆ§ËØÅ‰∏≠Èó¥‰ª∂
  io.use(async (socket: AuthenticatedSocket, next) => {
    try {
      const token = socket.handshake.auth.token || socket.handshake.headers.authorization?.split(' ')[1];

      if (!token) {
        return next(new Error('Authentication error: No token provided'));
      }

      const payload = verifyAccessToken(token);
      socket.userId = payload.userId;
      socket.username = payload.username;

      next();
    } catch (error) {
      next(new Error('Authentication error: Invalid token'));
    }
  });

  io.on('connection', async (socket: AuthenticatedSocket) => {
    const userId = socket.userId!;
    const socketId = socket.id;

    logger.info(`User connected: ${userId} (${socketId})`);

    // Â≠òÂÇ®Áî®Êà∑ socket Êò†Â∞Ñ
    userSockets.set(userId, socketId);

    // Êõ¥Êñ∞Áî®Êà∑Âú®Á∫øÁä∂ÊÄÅ
    try {
      await User.findByIdAndUpdate(userId, {
        isOnline: true,
        lastSeen: new Date(),
      });

      // Âú® Redis ‰∏≠Â≠òÂÇ®Âú®Á∫øÁä∂ÊÄÅ
      const redis = getRedisClient();
      await redis.set(`user:online:${userId}`, socketId, { EX: 3600 });
    } catch (error) {
      logger.error('Error updating online status:', error);
    }

    // Âä†ÂÖ•Áî®Êà∑Ëá™Â∑±ÁöÑÊàøÈó¥
    socket.join(`user:${userId}`);

    // ÂèëÈÄÅÊ∂àÊÅØ
    socket.on('send_message', async (data) => {
      try {
        const { conversationId, receiverId, type, content, mediaUrl, mediaSize, mediaDuration } = data;

        // ÂàõÂª∫Ê∂àÊÅØ
        const message = new Message({
          conversationId,
          senderId: userId,
          receiverId,
          type: type || 'text',
          content,
          mediaUrl,
          mediaSize,
          mediaDuration,
        });

        await message.save();

        // ÂèëÈÄÅÁªôÊé•Êî∂ËÄÖ
        if (receiverId) {
          io.to(`user:${receiverId}`).emit('new_message', message);
        }

        // ÂèëÈÄÅÁªôÁæ§ÁªÑÊàêÂëòÔºàÂ¶ÇÊûúÊòØÁæ§ËÅäÔºâ
        io.to(`conversation:${conversationId}`).emit('new_message', message);

        // Á°ÆËÆ§ÂèëÈÄÅÊàêÂäü
        socket.emit('message_sent', {
          success: true,
          messageId: message._id,
        });

        logger.info(`Message sent: ${message._id}`);
      } catch (error: any) {
        logger.error('Send message error:', error);
        socket.emit('message_error', {
          success: false,
          message: error.message,
        });
      }
    });

    // Âä†ÂÖ•‰ºöËØùÊàøÈó¥
    socket.on('join_conversation', (conversationId: string) => {
      socket.join(`conversation:${conversationId}`);
      logger.info(`User ${userId} joined conversation: ${conversationId}`);
    });

    // Á¶ªÂºÄ‰ºöËØùÊàøÈó¥
    socket.on('leave_conversation', (conversationId: string) => {
      socket.leave(`conversation:${conversationId}`);
      logger.info(`User ${userId} left conversation: ${conversationId}`);
    });

    // Ê≠£Âú®ËæìÂÖ•
    socket.on('typing', (data: { conversationId: string; receiverId?: string }) => {
      if (data.receiverId) {
        io.to(`user:${data.receiverId}`).emit('user_typing', {
          userId,
          conversationId: data.conversationId,
        });
      } else {
        socket.to(`conversation:${data.conversationId}`).emit('user_typing', {
          userId,
          conversationId: data.conversationId,
        });
      }
    });

    // ÂÅúÊ≠¢ËæìÂÖ•
    socket.on('stop_typing', (data: { conversationId: string; receiverId?: string }) => {
      if (data.receiverId) {
        io.to(`user:${data.receiverId}`).emit('user_stop_typing', {
          userId,
          conversationId: data.conversationId,
        });
      } else {
        socket.to(`conversation:${data.conversationId}`).emit('user_stop_typing', {
          userId,
          conversationId: data.conversationId,
        });
      }
    });

    // Ê∂àÊÅØÂ∑≤ËØªÂõûÊâß
    socket.on('message_read', async (data: { messageId: string; conversationId: string }) => {
      try {
        const message = await Message.findById(data.messageId);
        if (message && message.receiverId === userId) {
          message.isRead = true;
          await message.save();

          // ÈÄöÁü•ÂèëÈÄÅËÄÖ
          io.to(`user:${message.senderId}`).emit('message_read_receipt', {
            messageId: data.messageId,
            conversationId: data.conversationId,
            readBy: userId,
          });
        }
      } catch (error) {
        logger.error('Message read error:', error);
      }
    });

    // ÂøÉË∑≥
    socket.on('ping', () => {
      socket.emit('pong');
    });

    // Êñ≠ÂºÄËøûÊé•
    socket.on('disconnect', async () => {
      logger.info(`User disconnected: ${userId} (${socketId})`);

      // ÁßªÈô§Áî®Êà∑ socket Êò†Â∞Ñ
      userSockets.delete(userId);

      // Êõ¥Êñ∞Áî®Êà∑Á¶ªÁ∫øÁä∂ÊÄÅ
      try {
        await User.findByIdAndUpdate(userId, {
          isOnline: false,
          lastSeen: new Date(),
        });

        // ‰ªé Redis ‰∏≠Âà†Èô§Âú®Á∫øÁä∂ÊÄÅ
        const redis = getRedisClient();
        await redis.del(`user:online:${userId}`);
      } catch (error) {
        logger.error('Error updating offline status:', error);
      }
    });
  });

  logger.info('Socket.IO handlers set up successfully');
}

/**
 * Ëé∑ÂèñÁî®Êà∑ÁöÑ socket ID
 */
export function getUserSocketId(userId: string): string | undefined {
  return userSockets.get(userId);
}

/**
 * Ê£ÄÊü•Áî®Êà∑ÊòØÂê¶Âú®Á∫ø
 */
export function isUserOnline(userId: string): boolean {
  return userSockets.has(userId);
}


