import asyncio
from typing import Optional, Dict, Any
from loguru import logger

from backend.db.mongodb import MongoDBBase
from .models import LLMIORecord


class LLMIOAnalysisService:
    """记录 LLM 输入输出到 MongoDB（llm_io_analysis 集合）。"""

    def __init__(self):
        self._db = MongoDBBase("llm_io_analysis")

    async def record(self, *, session_id: str, source: str, prompt: str, output: str, meta: Optional[Dict[str, Any]] = None) -> Optional[str]:
        try:
            doc = LLMIORecord(
                session_id=session_id,
                source=source,
                prompt=prompt,
                output=output,
                meta=meta or {},
            )
            result = await self._db.collection.insert_one(doc.model_dump(by_alias=True))
            return str(result.inserted_id)
        except Exception as e:
            logger.warning(f"LLMIOAnalysisService.record failed: {e}")
            return None

    def record_background(self, *, session_id: str, source: str, prompt: str, output: str, meta: Optional[Dict[str, Any]] = None):
        """以后台任务方式记录，避免阻塞主流程。"""
        async def _runner():
            try:
                await self.record(session_id=session_id, source=source, prompt=prompt, output=output, meta=meta)
            except Exception as e:
                logger.warning(f"LLMIOAnalysisService.record_background failed: {e}")

        try:
            loop = asyncio.get_running_loop()
            loop.create_task(_runner())
        except RuntimeError:
            # 无事件循环时，忽略后台写入
            logger.warning("No running event loop; skip background LLM IO recording.")


# 单例式简便访问
_global_service: Optional[LLMIOAnalysisService] = None


def get_llm_io_analysis_service() -> LLMIOAnalysisService:
    global _global_service
    if _global_service is None:
        _global_service = LLMIOAnalysisService()
    return _global_service
