---
import Layout from "../../layouts/Layout.astro";
import { getCollection, getEntry } from 'astro:content';
import { navDocs, type DocId } from '../../config/docs-nav';

// Get static paths for all docs
export async function getStaticPaths() {
    const docs = await getCollection('docs');
    return docs.map((doc) => ({
        params: { slug: doc.slug },
    }));
}

// Resolve markdown content by slug
const { slug } = Astro.params as { slug: DocId };
const entry = await getEntry('docs', slug);
if (!entry) {
    throw new Error(`Doc not found for slug: ${slug}`);
}
const { Content } = await entry.render();
const title = `Docs - ${navDocs.find((d) => d.id === slug)?.label ?? "Docs"}`;
const description = `InnovatorBench ${slug} document`;
---

<Layout {title} {description}>
    <section class="max-w-7xl mx-auto px-6 py-16" id="docs-detail">
        <div class="docs__layout">
            <aside class="docs__sidebar docs__sidebar--left">
                <nav aria-label="Docs navigation">
                    <ul>
                        {
                            navDocs.map((d) => (
                                <li>
                                    <a 
                                        href={d.id === "tasks" ? "/tasks" : `/docs/${d.id}`}
                                        class={d.id === slug ? "nav-link--active" : ""}
                                    >
                                        {d.label}
                                    </a>
                                </li>
                            ))
                        }
                    </ul>
                </nav>
            </aside>

            <main class="docs__content">
                <article class="markdown-body" id="doc-content">
                    <Content />
                </article>
            </main>

            <aside class="docs__sidebar docs__sidebar--right">
                <nav aria-label="On this page">
                    <div class="toc-group">
                        <div class="toc-group__title">On this page</div>
                        <ul id="toc-list"></ul>
                    </div>
                </nav>
            </aside>
        </div>

        <style>
            .docs__layout {
                display: grid;
                grid-template-columns: 220px 1fr 280px;
                gap: 24px;
            }
            .docs__sidebar {
                position: sticky;
                top: 80px;
                align-self: start;
                max-height: calc(100vh - 100px);
                overflow: auto;
            }
            .docs__sidebar ul {
                list-style: none;
                padding: 0;
                margin: 0;
            }
            .docs__sidebar a {
                text-decoration: none;
            }
            .docs__sidebar--left a {
                color: var(--text-mid);
                padding: 0.5rem 0.75rem;
                border-radius: 8px;
                display: block;
                transition: color 0.18s ease, background 0.18s ease;
            }
            .docs__sidebar--left a:hover {
                color: var(--text-high);
                background: rgba(102, 184, 255, 0.1);
            }
            .docs__sidebar--left a.nav-link--active {
                color: var(--primary);
                background: rgba(102, 184, 255, 0.15);
                font-weight: 600;
            }
            .docs__sidebar--right a {
                color: var(--text-mid);
            }
            .toc-group {
                margin-bottom: 16px;
            }
            .toc-group__title {
                font-weight: 600;
                margin-bottom: 8px;
            }
            @media (max-width: 1024px) {
                .docs__layout {
                    grid-template-columns: 1fr;
                }
                .docs__sidebar {
                    position: static;
                    max-height: none;
                }
            }
        </style>

        <script>
            // Build right-side TOC from headings inside markdown-body
            const container = document.getElementById("doc-content");
            const tocList = document.getElementById("toc-list");
            if (container && tocList) {
                const headings = Array.from(
                    container.querySelectorAll("h2, h3"),
                );
                headings.forEach((h) => {
                    if (!h.id) {
                        const raw = h.textContent || "";
                        h.id = raw
                            .toLowerCase()
                            .trim()
                            .replace(/[^\w\s-]/g, "")
                            .replace(/\s+/g, "-")
                            .replace(/-+/g, "-");
                    }
                    const li = document.createElement("li");
                    li.style.marginLeft =
                        h.tagName.toLowerCase() === "h3" ? "12px" : "0";
                    const a = document.createElement("a");
                    a.href = `#${h.id}`;
                    a.textContent = h.textContent || "";
                    li.appendChild(a);
                    tocList.appendChild(li);
                });

                // Optional: highlight current heading
                const observer = new IntersectionObserver(
                    (entries) => {
                        entries.forEach((entry) => {
                            const id = entry.target.getAttribute("id");
                            if (!id) return;
                            const link = tocList.querySelector(
                                `a[href="#${id}"]`,
                            );
                            if (!link) return;
                            if (entry.isIntersecting) {
                                tocList
                                    .querySelectorAll("a.active")
                                    .forEach((el) =>
                                        el.classList.remove("active"),
                                    );
                                link.classList.add("active");
                            }
                        });
                    },
                    { rootMargin: "0px 0px -70% 0px", threshold: 0.1 },
                );
                headings.forEach((h) => observer.observe(h));
            }
        </script>
    </section>
</Layout>

