@import "tailwindcss";

/* Custom CSS Variables for consistent theming */
:root {
  --page-bg: #040810;
  --surface: rgba(12, 20, 38, 0.7);
  --surface-strong: rgba(16, 28, 52, 0.85);
  --glass-border: rgba(120, 178, 255, 0.2);
  --primary: #66b8ff;
  --primary-strong: #4da3ff;
  --secondary: #7b7fff;
  --accent: #ff8a65;
  --text-high: #f5f7ff;
  --text-mid: #c3c9d8;
  --text-low: #8892b0;
  --card-shadow: 0 18px 40px rgba(2, 9, 26, 0.45);
  --font-sans: "Inter", "Segoe UI", "Helvetica Neue", Arial, sans-serif;
}

/* Base styles */
* {
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
  margin: 0;
  min-height: 100vh;
  background: radial-gradient(circle at top right, rgba(88, 132, 255, 0.32), transparent 55%),
    radial-gradient(circle at 15% 20%, rgba(255, 138, 101, 0.18), transparent 45%),
    var(--page-bg);
  background-attachment: fixed;
  color: var(--text-high);
  font-family: var(--font-sans);
  line-height: 1.6;
  -webkit-font-smoothing: antialiased;
  padding-bottom: 4rem;
  padding-top: 64px;
}

a {
  color: inherit;
  text-decoration: none;
}

/* Navigation styles - keeping custom nav since it's complex */
.nav {"
