@router.get("/{document_id}/backups")
async def get_document_backups(
    document_id: str,
    limit: int = 10,
    current_user: UserDB = Depends(get_current_active_user)
):
    """
    🔧 新增：获取文档备份列表

    返回文档的备份历史，用于数据恢复。
    """
    try:
        collaboration_server = get_collaboration_server()
        if not collaboration_server or not collaboration_server.is_running:
            raise HTTPException(status_code=503, detail="协作服务不可用")

        # 从MongoDB获取备份列表
        backups_cursor = collaboration_server.document_manager.storage.db.collaboration_backups.find(
            {"document_id": document_id}
        ).sort("created_at", -1).limit(limit)

        backups = []
        async for backup in backups_cursor:
            backups.append({
                "id": str(backup["_id"]),
                "created_at": backup["created_at"].isoformat(),
                "backup_type": backup.get("backup_type", "unknown"),
                "content_preview": backup.get("content", "")[:100] + "..." if len(backup.get("content", "")) > 100 else backup.get("content", "")
            })

        return {
            "success": True,
            "document_id": document_id,
            "backups": backups,
            "total": len(backups)
        }

    except HTTPException:
        raise
    except Exception as e:
        logger.error(f"获取文档备份失败: {e}")
        raise HTTPException(status_code=500, detail="获取备份列表失败")

@router.post("/{document_id}/restore")
async def restore_document_from_backup(
    document_id: str,
    backup_id: str = Body(..., embed=True),
    current_user: UserDB = Depends(get_current_active_user)
):
    """
    🔧 新增：从备份恢复文档

    从指定备份恢复文档内容。
    """
    try:
        collaboration_server = get_collaboration_server()
        if not collaboration_server or not collaboration_server.is_running:
            raise HTTPException(status_code=503, detail="协作服务不可用")

        # 查找指定的备份
        from bson import ObjectId
        backup = await collaboration_server.document_manager.storage.db.collaboration_backups.find_one(
            {"_id": ObjectId(backup_id), "document_id": document_id}
        )

        if not backup:
            raise HTTPException(status_code=404, detail="备份不存在")

        # 获取文档实例
        ydoc = await collaboration_server.document_manager.get_document(document_id)

        text = ydoc.get('content', type=Y.Text)
        current_length = len(text.to_py()) if hasattr(text, 'to_py') else 0
        if current_length > 0:
            text.clear()
        text.insert(0, backup.get("content", ""))

        # 保存恢复后的文档
        await collaboration_server.document_manager.storage.save_document_with_backup(
            document_id, 
            {
                "content": backup.get("content", ""),
                "updated_at": datetime.utcnow(),
                "updated_by": str(current_user.id),
                "updated_by_name": current_user.username
            }
        )

        return {
            "success": True,
            "message": "文档恢复成功",
            "document_id": document_id,
            "restored_from": backup_id,
            "restored_by": str(current_user.id)
        }

    except HTTPException:
        raise
    except Exception as e:
        logger.error(f"恢复文档失败: {e}")
        raise HTTPException(status_code=500, detail="文档恢复失败")

@router.get("/health")
async def collaboration_health_check():
    """
    协作服务健康检查

    检查协作服务的运行状态和依赖项。
    """
    try:
        collaboration_server = get_collaboration_server()

        # 检查Y.js依赖
        yjs_available = True
        try:
            import ypy
        except ImportError:
            yjs_available = False

        if not collaboration_server:
            return {
                "status": "error",
                "message": "协作服务器未初始化",
                "yjs_available": yjs_available,
                "server_running": False
            }

        server_running = collaboration_server.is_running

        return {
            "status": "ok" if server_running and yjs_available else "degraded",
            "message": "协作服务正常" if server_running else "协作服务不可用",
            "yjs_available": yjs_available,
            "server_running": server_running,
            "features": {
                "real_time_collaboration": server_running and yjs_available,
                "document_snapshots": server_running,
                "user_awareness": server_running and yjs_available,
                "backup_and_restore": server_running  # 🔧 新增备份恢复功能标识
            }
        }

    except Exception as e:
        logger.error(f"健康检查失败: {e}")
        return {
            "status": "error",
            "message": f"健康检查失败: {str(e)}",
            "yjs_available": False,
            "server_running": False
        }

