    GoogleGenAI,
} from '@google/genai';
import { createCodeAssistContentGenerator } from '../code_assist/codeAssist.js';
import { UserTierId } from '../code_assist/types.js';
import { Config } from '../config/config.js';
import { SII_ENV_DEFAULTS } from '../config/defaults.js';
import { SIIEnvironmentManager } from '../config/siiEnvironmentManager.js';
import { getEffectiveModel } from './modelCheck.js';

/**
 * Interface abstracting the core functionalities for generating content and counting tokens.
 */
export interface ContentGenerator {
  generateContent(
    request: GenerateContentParameters,
  ): Promise<GenerateContentResponse>;

  generateContentStream(
    request: GenerateContentParameters,
  ): Promise<AsyncGenerator<GenerateContentResponse>>;

  countTokens(request: CountTokensParameters): Promise<CountTokensResponse>;

  embedContent(request: EmbedContentParameters): Promise<EmbedContentResponse>;

  userTier?: UserTierId;
}

export enum AuthType {
  LOGIN_WITH_GOOGLE = 'oauth-personal',
  USE_GEMINI = 'gemini-api-key',
  USE_VERTEX_AI = 'vertex-ai',
  CLOUD_SHELL = 'cloud-shell',
  USE_OPENAI = 'openai',
  USE_SII = 'sii',
  USE_OPENAI_WITH_SII_TOOLS = 'openai-with-sii-tools', // Hybrid mode: OpenAI for generation, SII for tools
}

export type ContentGeneratorConfig = {
  model: string;
  apiKey?: string;
  vertexai?: boolean;
  authType?: AuthType | undefined;
  enableOpenAILogging?: boolean;
  // Timeout configuration in milliseconds
  timeout?: number;
  // Maximum retries for failed requests
  maxRetries?: number;
  samplingParams?: {
    top_p?: number;
    top_k?: number;
    repetition_penalty?: number;
    presence_penalty?: number;
    frequency_penalty?: number;
    temperature?: number;
    max_tokens?: number;
  };
  proxy?: string | undefined;
};

export function createContentGeneratorConfig(
  config: Config,
  authType: AuthType | undefined,
): ContentGeneratorConfig {
  // è·å– SII ç¯å¢ƒå˜é‡é…ç½®
  const siiConfig = SIIEnvironmentManager.getSIIEnvironmentConfig();

  // ä¿ç•™é OpenAI çš„ç¯å¢ƒå˜é‡ï¼ˆç”¨äºå…¶ä»–è®¤è¯æ–¹å¼ï¼‰
  const geminiApiKey = process.env.GEMINI_API_KEY || undefined;
  const googleApiKey = process.env.GOOGLE_API_KEY || undefined;
  const googleCloudProject = process.env.GOOGLE_CLOUD_PROJECT || undefined;
  const googleCloudLocation = process.env.GOOGLE_CLOUD_LOCATION || undefined;

  // Use runtime model from config if available; otherwise, fall back to parameter
  const effectiveModel = config.getModel() || '';

  const contentGeneratorConfig: ContentGeneratorConfig = {
    model: effectiveModel,
    authType,
    proxy: config?.getProxy(),
    enableOpenAILogging: config.getEnableOpenAILogging(),
    timeout: config.getContentGeneratorTimeout(),
    maxRetries: config.getContentGeneratorMaxRetries(),
    samplingParams: config.getSamplingParams(),
  };

  // If we are using Google auth or we are in Cloud Shell, there is nothing else to validate for now
  if (
    authType === AuthType.LOGIN_WITH_GOOGLE ||
    authType === AuthType.CLOUD_SHELL
  ) {
    return contentGeneratorConfig;
  }

  if (authType === AuthType.USE_GEMINI && geminiApiKey) {
    contentGeneratorConfig.apiKey = geminiApiKey;
    contentGeneratorConfig.vertexai = false;
    getEffectiveModel(
      contentGeneratorConfig.apiKey,
      contentGeneratorConfig.model,
      contentGeneratorConfig.proxy,
    );

    return contentGeneratorConfig;
  }

  if (
    authType === AuthType.USE_VERTEX_AI &&
    (googleApiKey || (googleCloudProject && googleCloudLocation))
  ) {
    contentGeneratorConfig.apiKey = googleApiKey;
    contentGeneratorConfig.vertexai = true;

    return contentGeneratorConfig;
  }

  if (authType === AuthType.USE_OPENAI && siiConfig.apiKey) {
    contentGeneratorConfig.apiKey = siiConfig.apiKey;

    // OpenAI ç™»å½•ä¼˜å…ˆçº§ï¼šSII_OPENAI_MODEL â†’ SII_MODEL â†’ é»˜è®¤å€¼
    const openaiModel = process.env.SII_OPENAI_MODEL || 
                        process.env.SII_MODEL || 
                        effectiveModel || 
                        SII_ENV_DEFAULTS.SII_OPENAI_MODEL;
    contentGeneratorConfig.model = openaiModel;

    return contentGeneratorConfig;
  }

  if (authType === AuthType.USE_SII) {
    // SIIè®¤è¯ä¸éœ€è¦åœ¨é…ç½®ä¸­æŒ‡å®štokenï¼Œä¼šåŠ¨æ€è·å–
    return contentGeneratorConfig;
  }

  if (authType === AuthType.USE_OPENAI_WITH_SII_TOOLS && siiConfig.apiKey) {
    // Hybrid mode: Use OpenAI API key for content generation
    contentGeneratorConfig.apiKey = siiConfig.apiKey;

    // OpenAI ç™»å½•ä¼˜å…ˆçº§ï¼šSII_OPENAI_MODEL â†’ SII_MODEL â†’ é»˜è®¤å€¼
    const openaiModel = process.env.SII_OPENAI_MODEL || 
                        process.env.SII_MODEL || 
                        effectiveModel || 
                        SII_ENV_DEFAULTS.SII_OPENAI_MODEL;
    contentGeneratorConfig.model = openaiModel;

    return contentGeneratorConfig;
  }

  return contentGeneratorConfig;
}

export async function createContentGenerator(
  config: ContentGeneratorConfig,
  gcConfig: Config,
  sessionId?: string,
  notificationHandler?: any, // Import type later to avoid circular dependency
): Promise<ContentGenerator> {
  const version = process.env.CLI_VERSION || process.version;
  const httpOptions = {
    headers: {
      'User-Agent': `GeminiCLI/${version} (${process.platform}; ${process.arch})`,
    },
  };
  if (
    config.authType === AuthType.LOGIN_WITH_GOOGLE ||
    config.authType === AuthType.CLOUD_SHELL
  ) {
    return createCodeAssistContentGenerator(
      httpOptions,
      config.authType,
      gcConfig,
      sessionId,
    );
  }

  if (
    config.authType === AuthType.USE_GEMINI ||
    config.authType === AuthType.USE_VERTEX_AI
  ) {
    const googleGenAI = new GoogleGenAI({
      apiKey: config.apiKey === '' ? undefined : config.apiKey,
      vertexai: config.vertexai,
      httpOptions,
    });

    return googleGenAI.models;
  }

  if (config.authType === AuthType.USE_OPENAI) {
    if (!config.apiKey) {
      throw new Error('OpenAI API key is required');
    }

    // Import OpenAIContentGenerator dynamically to avoid circular dependencies
    const { OpenAIContentGenerator } = await import(
      './openaiContentGenerator.js'
    );

    // Always use OpenAIContentGenerator, logging is controlled by enableOpenAILogging flag
    return new OpenAIContentGenerator(config.apiKey, config.model, gcConfig);
  }

  if (config.authType === AuthType.USE_SII) {
    // Import SIIContentGenerator dynamically to avoid circular dependencies
    const { SIIContentGenerator } = await import('./siiContentGenerator.js');

    // Pass undefined for token since SII will acquire it dynamically
    return new SIIContentGenerator(
      undefined,
      config.model,
      gcConfig,
      notificationHandler,
    );
  }

  if (config.authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
    // Hybrid mode: Use OpenAI for content generation
    if (!config.apiKey) {
      throw new Error('OpenAI API key is required for hybrid mode');
    }

    // Import OpenAIContentGenerator dynamically to avoid circular dependencies
    const { OpenAIContentGenerator } = await import(
      './openaiContentGenerator.js'
    );

    // Use OpenAI for content generation (user's API key)
    return new OpenAIContentGenerator(config.apiKey, config.model, gcConfig);
  }

  throw new Error(
    `Error creating contentGenerator: Unsupported authType: ${config.authType}`,
  );
}

