import { Config } from '../config/config.js';
import { AuthType } from '../core/contentGenerator.js';
import { SIICognitionExtractUploadTool } from './sii-cognition-extract-upload.js';
import { SIICognitionsTool } from './sii-cognitions.js';
import { SIIDeepResearchTool } from './sii-deep-research.js';
import { SIIEmailNotificationTool } from './sii-email-notification.js';
import { SIIHybridSearchTool } from './sii-hybrid-search.js';
import { SIIWebFetchTool } from './sii-web-fetch.js';
import { SIIWebSearchTool } from './sii-web-search.js';
import { ToolRegistry } from './tool-registry.js';
import { Tool } from './tools.js';
// WebFetchTool and WebSearchTool (Google/Gemini-based) have been deprecated.
// These tools are no longer registered in any authentication mode.
// SII-based tools (SIIWebFetchTool, SIIWebSearchTool) are used instead.
// YoutubeAnalyseTool has been unregistered and removed from default tool definitions.
// The tool implementation still exists in source for reference/history, but it will
// not be created or registered by AuthToolManager. This keeps the compiled artifacts
// and tests intact while preventing the tool from being exposed at runtime.

/**
 * Interface for defining authentication-dependent tool configurations
 */
export interface AuthToolDefinition {
  /** Unique identifier for this tool type */
  id: string;

  /** Tool names that should be unregistered when refreshing */
  toolNames: string[];

  /** Function to create the appropriate tool based on auth type */
  createTool: (authType: AuthType | undefined, config: Config) => Tool | null;

  /** Optional function to get tool information */
  getToolInfo?: (authType: AuthType | undefined) => {
    name: string;
    description: string;
    limitations?: string;
  };
}

/**
 * Manager for authentication-dependent tools
 * Provides a unified way to register, refresh, and manage tools based on authentication type
 */
export class AuthToolManager {
  private toolDefinitions: Map<string, AuthToolDefinition> = new Map();

  constructor(private config: Config) {
    this.initializeDefaultToolDefinitions();
  }

  /**
   * Initialize default tool definitions for built-in auth-dependent tools
   */
  private initializeDefaultToolDefinitions(): void {
    // Search tool definition (SII-only - Google Search is deprecated)
    this.registerToolDefinition({
      id: 'search',
      toolNames: ['sii_web_search'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          return new SIIWebSearchTool(config);
        }
        // Google Search tool is deprecated, return null for other auth types
        return null;
      },
      getToolInfo: (authType: AuthType | undefined) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          return {
            name: 'SII Search',
            description: 'Enhanced search service with browsing capabilities',
          };
        }
        return {
          name: 'Web Search',
          description: 'Web search capabilities (requires SII authentication)',
          limitations: 'For web search capabilities, use SII authentication',
        };
      },
    });

    // Web fetch tool definition (SII-only - Standard Web Fetch is deprecated)
    this.registerToolDefinition({
      id: 'web_fetch',
      toolNames: ['sii_web_fetch'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          return new SIIWebFetchTool(config);
        }
        // Standard Web Fetch tool is deprecated, return null for other auth types
        return null;
      },
      getToolInfo: (authType: AuthType | undefined) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          return {
            name: 'SII Web Fetch',
            description:
              'Enhanced web content fetching service via SII backend',
          };
        }
        return {
          name: 'Web Fetch',
          description: 'Web content fetching capabilities (requires SII authentication)',
          limitations: 'For web fetching capabilities, use SII authentication',
        };
      },
    });

    // Cognitions tool definition (SII-only, now also available in hybrid mode)
    this.registerToolDefinition({
      id: 'cognitions',
      toolNames: ['sii_cognitions'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          // console.log(`[DEBUG] Creating SIICognitionsTool instance`);
          return new SIICognitionsTool(config);
        }
        // console.log(`[DEBUG] AuthType does not match USE_SII or USE_OPENAI_WITH_SII_TOOLS, returning null`);
        return null;
      },
    });

    // Deep Research tool definition (SII-only, now also available in hybrid mode)
    this.registerToolDefinition({
      id: 'deep_research',
      toolNames: ['sii_deep_research'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          // console.log(`[DEBUG] Creating SIIDeepResearchTool instance`);
          return new SIIDeepResearchTool(config);
        }
        return null;
      },
    });

    // Hybrid Search tool definition (SII-only, now also available in hybrid mode)
    this.registerToolDefinition({
      id: 'hybrid_search',
      toolNames: ['sii_hybrid_search'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          // console.log(`[DEBUG] Creating SIIHybridSearchTool instance`);
          return new SIIHybridSearchTool(config);
        }
        return null;
      },
    });

  // NOTE: The `youtube_analyse` tool has been intentionally unregistered.
  // Previously this block created and registered `YoutubeAnalyseTool` for SII auth.
  // We keep the implementation file in the repo for reference, but do not register
  // the tool here so it won't be exposed to the runtime tool registry.

    // Cognition Extract & Upload tool definition (SII-only, now also available in hybrid mode)
    this.registerToolDefinition({
      id: 'cognition_extract_upload',
      toolNames: ['sii_cognition_extract_upload'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          // console.log(`[DEBUG] Creating SIICognitionExtractUploadTool instance`);
          return new SIICognitionExtractUploadTool(config);
        }
        return null;
      },
      getToolInfo: () => ({
        name: 'SII Cognition Extract & Upload',
        description: 'Extract cognition information from web URLs and upload to SII backend. Automatically analyzes content, generates structured cognition data with bilingual support, and saves to the cognition database.',
      }),
    });

    // Email Notification tool definition (SII-only, now also available in hybrid mode)
    this.registerToolDefinition({
      id: 'email_notification',
      toolNames: ['sii_email_notification'],
      createTool: (authType: AuthType | undefined, config: Config) => {
        if (authType === AuthType.USE_SII || authType === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
          return new SIIEmailNotificationTool(config);
        }
        return null;
      },
      getToolInfo: () => ({
        name: 'SII Email Notification',
        description:
          'Send important email notifications to the authenticated user. Only under explicit request or remote monitoring + important events (critical failure, long job done, or action required).',
      }),
    });
  }

  /**
   * Register a new tool definition
   */
  registerToolDefinition(definition: AuthToolDefinition): void {
    this.toolDefinitions.set(definition.id, definition);
  }

  /**
   * Unregister a tool definition
   */
  unregisterToolDefinition(id: string): void {
    this.toolDefinitions.delete(id);
  }

  /**
   * Register all authentication-dependent tools to the registry
   */
  registerAllTools(
    registry: ToolRegistry,
    authType: AuthType | undefined,
  ): void {
    // console.log(
    //   `[DEBUG] AuthToolManager: Registering tools for authType: ${authType}`,
    // );

    for (const [id, definition] of this.toolDefinitions) {
      const tool = definition.createTool(authType, this.config);
      if (tool) {
        // console.log(`[DEBUG] Registering ${id} tool: ${tool.name}`);
        registry.registerTool(tool);
      } else {
        // console.log(
        //   `[DEBUG] No tool created for ${id} with authType: ${authType}`,
        // );
      }
    }
  }

  /**
   * Refresh all authentication-dependent tools in the registry
   */
  async refreshAllTools(registry: ToolRegistry): Promise<void> {
    const authType = this.config.getContentGeneratorConfig()?.authType;
    // console.log(
    //   `[DEBUG] AuthToolManager: Refreshing tools for authType: ${authType}`,
    // );

    // Remove all existing auth-dependent tools
    for (const definition of this.toolDefinitions.values()) {
      for (const toolName of definition.toolNames) {
        if (registry.getTool(toolName)) {
          // console.log(`[DEBUG] Unregistering tool: ${toolName}`);
          registry.unregisterTool(toolName);
        }
      }
    }

    // Register new tools based on current auth type
    this.registerAllTools(registry, authType);

    // Update the client with the new tools
    const geminiClient = this.config.getGeminiClient();
    if (geminiClient) {
      await geminiClient.setTools();
    }
  }

  /**
   * Get tool information for a specific tool type
   */
  getToolInfo(toolId: string): {
    name: string;
    description: string;
    limitations?: string;
  } | null {
    const definition = this.toolDefinitions.get(toolId);
    if (!definition || !definition.getToolInfo) {
      return null;
    }

    const authType = this.config.getContentGeneratorConfig()?.authType;
    return definition.getToolInfo(authType);
  }

  /**
   * Get all registered tool definitions
   */
  getToolDefinitions(): Map<string, AuthToolDefinition> {
    return new Map(this.toolDefinitions);
  }
}

