      console.log(`[DEBUG] OpenAI API response received: ${response ? 'yes' : 'no'}`);
      console.log(`[DEBUG] Response has data: ${response?.data ? 'yes' : 'no'}`);

      if (response && response.data) {
        // OpenAI authentication successful
        console.log('[DEBUG] OpenAI authentication successful');
        const openaiStatus: AuthStatus = {
          isAuthenticated: true,
          method: (isHybridMode || hasSIICredentials ? 'OpenAI+SII' : 'OpenAI') as AuthStatus['method'],
          connectionStatus: 'connected' as const,
          lastChecked: new Date(),
          serviceInfo: {
            baseUrl: this.baseUrl || this.effectiveBaseUrl,
            model: this.model,
            availableModels: response.data.length,
            mode: isHybridMode ? 'Hybrid (OpenAI generation + SII tools)' : 'OpenAI only',
          },
        };
        console.log(`[DEBUG] Created openaiStatus with method: ${openaiStatus.method}`);

        // If in hybrid mode, also verify SII credentials
        if (isHybridMode && hasSIICredentials) {
          console.log('[DEBUG] Checking SII credentials in hybrid mode...');
          try {
            const siiBaseUrl = process.env.SII_BASE_URL || 'https://www.opensii.ai/backend';
            const username = process.env.SII_USERNAME!;
            const password = process.env.SII_PASSWORD!;
            console.log(`[DEBUG] SII base URL: ${siiBaseUrl}`);

            // Step 1: Login to get token
            console.log('[DEBUG] Attempting SII login...');
            const loginResponse = await fetch(`${siiBaseUrl}/auth/login`, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
              },
              body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`,
