import { AuthType, Config } from '@gair/sii-cli-core';
import { validateAuthMethod } from './config/auth.js';
import { USER_SETTINGS_PATH } from './config/settings.js';

function getAuthTypeFromEnv(): AuthType | undefined {
  if (process.env.GOOGLE_GENAI_USE_GCA === 'true') {
    return AuthType.LOGIN_WITH_GOOGLE;
  }
  if (process.env.GOOGLE_GENAI_USE_VERTEXAI === 'true') {
    return AuthType.USE_VERTEX_AI;
  }
  if (process.env.GEMINI_API_KEY) {
    return AuthType.USE_GEMINI;
  }
  // Check for hybrid mode: both OpenAI API key and SII credentials
  if (process.env.SII_OPENAI_API_KEY && process.env.SII_USERNAME && process.env.SII_PASSWORD) {
    return AuthType.USE_OPENAI_WITH_SII_TOOLS;
  }
  // Fallback to OpenAI-only mode if only API key is present
  if (process.env.SII_OPENAI_API_KEY) {
    return AuthType.USE_OPENAI;
  }
  // Check for SII-only mode
  if (process.env.SII_USERNAME && process.env.SII_PASSWORD) {
    return AuthType.USE_SII;
  }
  return undefined;
}

export async function validateNonInteractiveAuth(
  configuredAuthType: AuthType | undefined,
  nonInteractiveConfig: Config,
) {
  const effectiveAuthType = configuredAuthType || getAuthTypeFromEnv();

  if (!effectiveAuthType) {
    console.error(
      `Please set an Auth method in your ${USER_SETTINGS_PATH} or specify one of the following environment variables before running:\n` +
      `  - GEMINI_API_KEY (for Gemini API)\n` +
      `  - SII_OPENAI_API_KEY (for OpenAI API)\n` +
      `  - SII_OPENAI_API_KEY + SII_USERNAME + SII_PASSWORD (for hybrid mode: OpenAI generation + SII tools)\n` +
      `  - SII_USERNAME + SII_PASSWORD (for SII backend)\n` +
      `  - GOOGLE_GENAI_USE_VERTEXAI=true (for Vertex AI)\n` +
      `  - GOOGLE_GENAI_USE_GCA=true (for Google Cloud Auth)`,
    );
    process.exit(1);
  }

  const err = validateAuthMethod(effectiveAuthType);
  if (err != null) {
    console.error(err);
    process.exit(1);
  }

  await nonInteractiveConfig.refreshAuth(effectiveAuthType);
  return nonInteractiveConfig;
}

