import { AuthType } from '@gair/sii-cli-core';
import { loadEnvironment } from './settings.js';

export const validateAuthMethod = (authMethod: string): string | null => {
  loadEnvironment();

  console.log(`[DEBUG] validateAuthMethod called with: ${authMethod}`);

  if (
    authMethod === AuthType.LOGIN_WITH_GOOGLE ||
    authMethod === AuthType.CLOUD_SHELL
  ) {
    return null;
  }

  if (authMethod === AuthType.USE_GEMINI) {
    if (!process.env.GEMINI_API_KEY) {
      return 'GEMINI_API_KEY environment variable not found. Add that to your environment and try again (no reload needed if using .env)!';
    }
    return null;
  }

  if (authMethod === AuthType.USE_VERTEX_AI) {
    const hasVertexProjectLocationConfig =
      !!process.env.GOOGLE_CLOUD_PROJECT && !!process.env.GOOGLE_CLOUD_LOCATION;
    const hasGoogleApiKey = !!process.env.GOOGLE_API_KEY;
    if (!hasVertexProjectLocationConfig && !hasGoogleApiKey) {
      return (
        'When using Vertex AI, you must specify either:\n' +
        'â€¢ GOOGLE_CLOUD_PROJECT and GOOGLE_CLOUD_LOCATION environment variables.\n' +
        'â€¢ GOOGLE_API_KEY environment variable (if using express mode).\n' +
        'Update your environment and try again (no reload needed if using .env)!'
      );
    }
    return null;
  }

  if (authMethod === AuthType.USE_OPENAI) {
    if (!process.env.SII_OPENAI_API_KEY) {
      return 'SII_OPENAI_API_KEY environment variable not found. You can enter it interactively or add it to your .env file.';
    }
    return null;
  }

  if (authMethod === AuthType.USE_SII) {
    console.log(`[DEBUG] Checking SII environment variables:`);
    console.log(
      `[DEBUG] SII_USERNAME: ${process.env.SII_USERNAME ? 'SET' : 'NOT SET'}`,
    );
    console.log(
      `[DEBUG] SII_PASSWORD: ${process.env.SII_PASSWORD ? 'SET' : 'NOT SET'}`,
    );
    console.log(
      `[DEBUG] SII_BASE_URL: ${process.env.SII_BASE_URL || 'NOT SET'}`,
    );

    // Check for username/password as primary auth method
    if (!process.env.SII_USERNAME || !process.env.SII_PASSWORD) {
      return 'SII credentials not found. You can enter them interactively or add SII_USERNAME/SII_PASSWORD to your .env file.';
    }
    return null;
  }

  if (authMethod === AuthType.USE_OPENAI_WITH_SII_TOOLS) {
    console.log(`[DEBUG] Checking hybrid mode (OpenAI + SII tools) environment variables:`);
    console.log(
      `[DEBUG] SII_OPENAI_API_KEY: ${process.env.SII_OPENAI_API_KEY ? 'SET' : 'NOT SET'}`,
    );
    console.log(
      `[DEBUG] SII_USERNAME: ${process.env.SII_USERNAME ? 'SET' : 'NOT SET'}`,
    );
    console.log(
      `[DEBUG] SII_PASSWORD: ${process.env.SII_PASSWORD ? 'SET' : 'NOT SET'}`,
    );

    // Check for both OpenAI API key and SII credentials
    if (!process.env.SII_OPENAI_API_KEY) {
      return 'SII_OPENAI_API_KEY environment variable not found. You can enter it interactively or add it to your .env file.';
    }
    if (!process.env.SII_USERNAME || !process.env.SII_PASSWORD) {
      return 'SII credentials not found. You can enter them interactively or add SII_USERNAME/SII_PASSWORD to your .env file.';
    }
    return null;
  }

  return 'Invalid auth method selected.';
};

