import { ContentGenerator } from '../core/contentGenerator.js';
import { AuthStatus, AuthStatusProvider } from '../types/auth.js';

/**
 * Check if a content generator supports authentication status checking
 */
export function isAuthStatusProvider(
  generator: ContentGenerator,
): generator is ContentGenerator & AuthStatusProvider {
  return (
    'checkAuthStatus' in generator &&
    typeof generator.checkAuthStatus === 'function'
  );
}

/**
 * Get authentication status from a content generator
 */
export async function getAuthStatusFromGenerator(
  generator: ContentGenerator,
): Promise<AuthStatus | null> {
  if (isAuthStatusProvider(generator)) {
    try {
      const status = await generator.checkAuthStatus();
      return status;
    } catch (error) {
      return {
        isAuthenticated: false,
        method: 'Unknown',
        connectionStatus: 'error',
        error: error instanceof Error ? error.message : 'Status check failed',
        lastChecked: new Date(),
      };
    }
  }
  return null;
}

/**
 * Format authentication status for display in footer
 */
export function formatAuthStatusForFooter(status: AuthStatus | null): string {
  if (!status) {
    return 'â“ Auth: Unknown';
  }

  const statusIcon = getAuthStatusIcon(status);
  const methodName = status.method;
  const userInfo = status.username ? ` (${status.username})` : '';

  return `${statusIcon} Auth: ${methodName}${userInfo}`;
}

/**
 * Format authentication status for detailed display
 */
export function formatAuthStatusDetailed(status: AuthStatus): {
  summary: string;
  details: string[];
  color: 'green' | 'red' | 'yellow' | 'gray';
} {
  const statusIcon = getAuthStatusIcon(status);
  const summary = `${statusIcon} ${status.method} Authentication`;

  const details: string[] = [];

  // Check if this is hybrid mode
  const isHybridMode = status.method === 'OpenAI+SII';

  if (isHybridMode) {
    // Hybrid mode: Show both OpenAI and SII status
    details.push(`Mode: Hybrid (OpenAI generation + SII tools)`);
    details.push(``); // Empty line for separation

    // OpenAI status
    if (status.isAuthenticated) {
      details.push(`OpenAI: âœ… Connected`);
      if (status.serviceInfo?.baseUrl) {
        details.push(`  Server: ${status.serviceInfo.baseUrl}`);
      }
      if (status.serviceInfo?.model) {
        details.push(`  Model: ${status.serviceInfo.model}`);
      }
    } else {
      details.push(`OpenAI: âŒ ${status.connectionStatus}`);
      if (status.error) {
        details.push(`  Error: ${status.error}`);
      }
    }

    details.push(``); // Empty line for separation

    // SII status
    const siiStatus = status.serviceInfo?.siiStatus;
    if (siiStatus === 'connected') {
      details.push(`SII: âœ… Connected`);
      if (status.serviceInfo?.siiUsername) {
        details.push(`  User: ${status.serviceInfo.siiUsername}`);
      }
      if (status.serviceInfo?.balance !== undefined) {
        details.push(`  Balance: ${status.serviceInfo.balance} tokens`);
      }
      if (status.serviceInfo?.userLevel) {
        details.push(`  Level: ${status.serviceInfo.userLevel}`);
      }
    } else if (siiStatus === 'failed') {
      details.push(`SII: âŒ Failed`);
      if (status.serviceInfo?.siiError) {
        details.push(`  Error: ${status.serviceInfo.siiError}`);
      }
    } else if (siiStatus === 'error') {
      details.push(`SII: âš ï¸  Error`);
      if (status.serviceInfo?.siiError) {
        details.push(`  Error: ${status.serviceInfo.siiError}`);
      }
    } else {
      details.push(`SII: â“ Not checked`);
    }
  } else {
    // Non-hybrid mode: Show standard status
    if (status.isAuthenticated) {
      details.push(`Status: Connected`);
      if (status.username) {
        details.push(`User: ${status.username}`);
      }
      if (status.serviceInfo?.baseUrl) {
        details.push(`Server: ${status.serviceInfo.baseUrl}`);
      }
      if (status.serviceInfo?.model) {
        details.push(`Model: ${status.serviceInfo.model}`);
      }
      if (status.serviceInfo?.balance !== undefined) {
        details.push(`Balance: ${status.serviceInfo.balance} tokens`);
      }
    } else {
      details.push(`Status: ${status.connectionStatus}`);
      if (status.error) {
        details.push(`Error: ${status.error}`);
      }
    }
  }

  if (status.lastChecked) {
    details.push(`Last checked: ${status.lastChecked.toLocaleString()}`);
  }

  const color = getAuthStatusColor(status);

  return { summary, details, color };
}

/**
 * Get appropriate icon for authentication status
 */
export function getAuthStatusIcon(status: AuthStatus): string {
  if (status.isAuthenticated && status.connectionStatus === 'connected') {
    return '[OK]';
  }

  switch (status.connectionStatus) {
    case 'checking':
      return 'â³';
    case 'failed':
      return 'âŒ';
    case 'error':
      return 'âš ï¸';
    default:
      return 'â“';
  }
}

/**
 * Get appropriate color for authentication status
 */
export function getAuthStatusColor(
  status: AuthStatus,
): 'green' | 'red' | 'yellow' | 'gray' {
  if (status.isAuthenticated && status.connectionStatus === 'connected') {
    return 'green';
  }

  switch (status.connectionStatus) {
    case 'checking':
      return 'yellow';
    case 'failed':
    case 'error':
      return 'red';
    default:
      return 'gray';
  }
}

/**
 * Get user-friendly error guidance based on authentication status
 */
export function getAuthErrorGuidance(status: AuthStatus): string | null {
  if (status.isAuthenticated) {
    return null;
  }

  const method = status.method;
  const error = status.error || '';

  // SII-specific guidance
  if (method === 'SII') {
    if (error.includes('401') || error.includes('authentication')) {
      return 'Please check your SII username and password. You can update them using environment variables SII_USERNAME and SII_PASSWORD.';
    }
    if (error.includes('connection') || error.includes('timeout')) {
      return 'Cannot reach SII server. Please check your internet connection or verify the server URL.';
    }
    if (error.includes('credentials not found')) {
      return 'SII credentials not configured. Please set SII_USERNAME and SII_PASSWORD environment variables.';
    }
    return 'Please verify your SII credentials and server configuration.';
  }

  // OpenAI-specific guidance
  if (method === 'OpenAI') {
    if (error.includes('401') || error.includes('Invalid API key')) {
      return 'Please check your OpenAI API key. You can update it using the SII_OPENAI_API_KEY environment variable.';
    }
    if (error.includes('403') || error.includes('forbidden')) {
      return 'Your OpenAI API key does not have access to the requested resource.';
    }
    if (error.includes('429') || error.includes('rate limit')) {
      return 'OpenAI API rate limit exceeded. Please wait before making more requests or check your usage quota.';
    }
    if (error.includes('timeout')) {
      return 'Connection to OpenAI API timed out. Please check your internet connection.';
    }
    return 'Please verify your OpenAI API key and configuration.';
  }

  // Hybrid mode guidance (OpenAI + SII Tools)
  if (method === 'OpenAI+SII' || method.toLowerCase().includes('hybrid')) {
    if (error.includes('OpenAI') || error.includes('API key')) {
      return 'OpenAI authentication failed. Please check your SII_OPENAI_API_KEY environment variable.';
    }
    if (error.includes('SII') || error.includes('credentials')) {
      return 'SII authentication failed. Please check your SII_USERNAME and SII_PASSWORD environment variables.';
    }
    return 'Hybrid mode requires both OpenAI API key (SII_OPENAI_API_KEY) and SII credentials (SII_USERNAME, SII_PASSWORD). Please verify both are configured correctly.';
  }

  // Generic guidance
  return 'Please check your authentication configuration and try again.';
}

