import { Config } from '@gair/sii-cli-core';
import WebSocket from 'ws';

export type OutgoingPayload = {
  agent?: string;
  payload: any;
};

export type IncomingMessage = {
  session_id?: string;
  content?: any;
  type?: string;
  timestamp?: string;
  message_id?: string;
  originator?: string;
};

export interface SiiWsClientOptions {
  baseUrl?: string;
  clientId: string;
  sessionIds?: string[];
  heartbeatIntervalMs?: number;
  onMessage?: (msg: IncomingMessage) => void;
  onOpen?: () => void;
  onClose?: (code: number, reason: string) => void;
  onError?: (err: unknown) => void;
}

/**
 * Minimal SII WebSocket client for CLI, mirroring SII frontend handshake.
 * - Auth via SIIClient (username/password -> token)
 * - Connect to /conversation/ws/{client_id}?token=...
 * - Send messages in {session_id, agent, payload}
 */
export class SiiWebSocketClient {
  private socket: WebSocket | null = null;
  private readonly config: Config;
  private readonly opts: SiiWsClientOptions;
  private reconnectAttempts = 0;
  private reconnectTimer: NodeJS.Timeout | null = null;
  private heartbeatTimer: NodeJS.Timeout | null = null;
  private siiBaseUrl: string;
  private siiToken: string | null = null;

  constructor(config: Config, opts: SiiWsClientOptions) {
    this.config = config;
    this.siiBaseUrl = opts.baseUrl || process.env.SII_BASE_URL || 'https://www.opensii.ai/backend';
    this.opts = opts;
    // console.log(`[DEBUG] SiiWebSocketClient initialized with baseUrl: ${this.siiBaseUrl}`);
  }

  async connect(): Promise<void> {
    await this.ensureDisconnected();
    const token = await this.ensureValidToken();
    const wsUrl = this.buildWsUrl(token);

    this.socket = new WebSocket(wsUrl);

    this.socket.on('open', () => {
      this.reconnectAttempts = 0;
      this.startHeartbeat();
      // Auto-subscribe all provided sessions after connect
      if (this.opts.sessionIds && this.opts.sessionIds.length > 0) {
        for (const sid of this.opts.sessionIds) {
          this.send({ agent: 'none', payload: { type: 'subscribe' } }, sid);
        }
      }
      this.opts.onOpen?.();
    });

    this.socket.on('message', (data: WebSocket.RawData) => {
      try {
        const text = data.toString('utf8');
        const msg = JSON.parse(text) as IncomingMessage;
        // Heartbeat handling is server driven; no special case needed here
        this.resetHeartbeat();
        this.opts.onMessage?.(msg);
      } catch (err) {
        this.opts.onError?.(err);
      }
    });

    this.socket.on('close', (code, buf) => {
      const reason = buf?.toString('utf8') || '';
      this.stopHeartbeat();
      this.opts.onClose?.(code, reason);
      this.scheduleReconnect();
    });

    this.socket.on('error', (err) => {
      this.opts.onError?.(err);
    });
  }

  private buildWsUrl(token: string): string {
    // Convert http://localhost:8257 -> ws://localhost:8257/cli/ws/{clientId}?token=...
    const base = this.siiBaseUrl;
    const url = new URL(base);
    const protocol = url.protocol === 'https:' ? 'wss:' : 'ws:';
    const host = url.host; // includes port if any
    const wsUrl = `${protocol}//${host}/cli/ws/${this.opts.clientId}?token=${encodeURIComponent(token)}`;
    // console.log(`[DEBUG] Connecting to WebSocket: ${wsUrl}`);
    return wsUrl;
  }

  private async ensureValidToken(forceRefresh = false): Promise<string> {
    if (forceRefresh) {
      this.siiToken = null;
      delete (process.env as any).SII_TOKEN;
    }
    if (this.siiToken && !forceRefresh) return this.siiToken;
    const envToken = process.env.SII_TOKEN;
    if (envToken && !forceRefresh) {
      this.siiToken = envToken;
      return envToken;
    }
    const username = process.env.SII_USERNAME;
    const password = process.env.SII_PASSWORD;
    if (!username || !password) {
      throw new Error('SII credentials not found. Set SII_USERNAME and SII_PASSWORD.');
    }
    // console.log(`[DEBUG] SiiWebSocketClient authenticating to: ${this.siiBaseUrl}/auth/login`);
    const resp = await fetch(`${this.siiBaseUrl}/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`,
    });
    if (!resp.ok) {
      const txt = await resp.text().catch(() => '');
      throw new Error(`SII auth failed (${resp.status}): ${txt}`);
    }
    const data = (await resp.json().catch(() => ({}))) as any;
    const token = data?.access_token;
    if (!token) throw new Error('SII auth response missing access_token');
    this.siiToken = token;
    process.env.SII_TOKEN = token;
    return token;
  }

  private scheduleReconnect() {
    if (this.reconnectTimer) return;
    const attempt = ++this.reconnectAttempts;
    const delay = Math.min(2000 * Math.pow(1.5, attempt - 1), 30000);
    this.reconnectTimer = setTimeout(async () => {
      this.reconnectTimer = null;
      try {
        await this.connect();
      } catch (err) {
        this.opts.onError?.(err);
        this.scheduleReconnect();
      }
    }, delay);
  }

  private startHeartbeat() {
    this.stopHeartbeat();
    const interval = this.opts.heartbeatIntervalMs ?? 3000;
    this.heartbeatTimer = setInterval(() => {
      // Server sends heartbeat; here we just ensure connection liveness via timer
      // If needed, could send ping frames: this.socket?.ping()
    }, interval + 1000);
  }

  private resetHeartbeat() {
    // No-op for now; placeholder to track time since last msg
  }

  private stopHeartbeat() {
    if (this.heartbeatTimer) {
      clearInterval(this.heartbeatTimer);
      this.heartbeatTimer = null;
    }
  }

  async ensureDisconnected() {
    if (!this.socket) return;
    await new Promise<void>((resolve) => {
      try {
        this.socket?.once('close', () => resolve());
        this.socket?.close(1000, 'reconnect');
        setTimeout(() => resolve(), 300);
      } catch {
        resolve();
      }
    });
    this.socket = null;
  }

  send(body: OutgoingPayload, sessionId: string) {
    if (!this.socket || this.socket.readyState !== WebSocket.OPEN) {
      throw new Error('WebSocket is not connected');
    }
    const frame = {
      session_id: sessionId,
      agent: body.agent || 'deep_cognition',
      payload: body,
    };
    this.socket.send(JSON.stringify(frame));
  }
}

