class WebSocketDebugLogger {
  private config: any;

  constructor(config: any) {
    this.config = config;
  }

  debug(message: string, ...args: any[]) {
    if (this.config.getDebugMode()) {
      console.log(`[DEBUG] ${message}`, ...args);
    }
  }

  error(message: string, ...args: any[]) {
    if (this.config.getDebugMode()) {
      console.error(`[DEBUG] ${message}`, ...args);
    }
  }

  info(message: string, ...args: any[]) {
    if (this.config.getDebugMode()) {
      console.log(`[INFO] ${message}`, ...args);
    }
  }
}

// ğŸ”§ å®Œå…¨é‡æ„ï¼šä½¿ç”¨ä¸ sii.tsx --connect-session-id æ¨¡å¼å®Œå…¨ä¸€è‡´çš„æ¶æ„
async function executeCommandAndStreamSimple(config: any, command: string, ws: any, sessionId: string, context: any) {
  let originalApprovalMode: ApprovalMode | undefined;

  try {
    // ğŸ”§ åˆ›å»ºé›†ä¸­å¼è°ƒè¯•æ—¥å¿—å™¨
    const logger = new WebSocketDebugLogger(config);

    // è®¾ç½®ä¸ºYOLOæ¨¡å¼ï¼ˆå”¯ä¸€ä¸äº¤äº’å¼æ¨¡å¼çš„å·®å¼‚ï¼‰
    originalApprovalMode = config.getApprovalMode();
    config.setApprovalMode(ApprovalMode.YOLO);

    const geminiClient = config.getGeminiClient();
    if (!geminiClient) {
      throw new Error('GeminiClient not available - please check authentication configuration');
    }

    const abortController = new AbortController();
    const prompt_id = `web-cmd-${Date.now()}`;

    logger.debug('Starting WebSocket command execution using interactive pattern');

    // ğŸ”§ å…³é”®ä¿®å¤ï¼šä½¿ç”¨ä¸äº¤äº’å¼æ¨¡å¼å®Œå…¨ç›¸åŒçš„æµç¨‹
    await processWebSocketCommandInteractive(
      geminiClient,
      [{ text: command }],
      abortController.signal,
      prompt_id,
      config,
      ws,
      sessionId,
      logger,
      context
    );

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);

    context.ui.addItem({ type: MessageType.ERROR, text: `âŒ [ERROR] ${errorMessage}` }, Date.now());

    ws.send({
      agent: 'cli_agent',
      payload: {
        type: 'execution_error',
        error: errorMessage,
        from: 'cli'
      },
    }, sessionId);
    throw error;
  } finally {
    if (originalApprovalMode !== undefined) {
      try {
        config.setApprovalMode(originalApprovalMode);
      } catch (e) {
        console.error('Failed to restore approval mode:', e);
      }
    }
  }
}

