// ğŸ”§ æ–°å¢ï¼šæ¨¡æ‹Ÿäº¤äº’å¼æ¨¡å¼çš„ submitQuery å‡½æ•°
async function submitWebSocketQuery(
  geminiClient: any,
  query: any,
  abortSignal: AbortSignal,
  prompt_id: string,
  config: any,
  ws: any,
  sessionId: string,
  allResponseText: string,
  isContinuation: boolean = false,
  logger: WebSocketDebugLogger,
  context: any
) {
  try {
    // ğŸ”§ å…³é”®ï¼šä½¿ç”¨ä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒçš„é«˜çº§ API
    const stream = geminiClient.sendMessageStream(
      query,
      abortSignal,
      prompt_id,
    );

    // ğŸ”§ å…³é”®ï¼šä½¿ç”¨ä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒçš„äº‹ä»¶å¤„ç†
    const processingResult = await processWebSocketStreamEvents(
      stream,
      abortSignal,
      config,
      ws,
      sessionId,
      allResponseText,
      logger,
      context
    );

    // ğŸ”§ å…³é”®ï¼šå¤„ç†å·¥å…·è°ƒç”¨ï¼ˆä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒçš„é€»è¾‘ï¼‰
    if (processingResult.toolCallRequests.length > 0) {
      await handleWebSocketToolCalls(
        processingResult.toolCallRequests,
        geminiClient,
        abortSignal,
        prompt_id,
        config,
        ws,
        sessionId,
        allResponseText,
        logger,
        context
      );
    } else {
      // ğŸ”§ å…³é”®ï¼šä½¿ç”¨ Next Speaker Check åˆ¤æ–­æ˜¯å¦ç»§ç»­ï¼ˆä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒï¼‰
      const shouldContinue = await checkWebSocketNextSpeaker(
        geminiClient,
        abortSignal,
        config,
        logger
      );

      if (shouldContinue) {
        logger.debug('Next speaker is model, continuing...');
        // é€’å½’ç»§ç»­å¯¹è¯
        await submitWebSocketQuery(
          geminiClient,
          [{ text: 'Please continue.' }],
          abortSignal,
          prompt_id,
          config,
          ws,
          sessionId,
          allResponseText,
          true,
          logger,
          context
        );
      } else {
        logger.debug('Conversation completed');
        // å‘é€å®Œæˆä¿¡å·
        ws.send({
          agent: 'cli_agent',
          payload: {
            type: 'execution_complete',
            final_response: allResponseText,
            from: 'cli'
          },
        }, sessionId);
      }
    }

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logger.error('Stream processing failed:', error);

    context.ui.addItem({ type: MessageType.ERROR, text: `âŒ [ERROR] ${errorMessage}` }, Date.now());

    ws.send({
      agent: 'cli_agent',
      payload: {
        type: 'execution_error',
        error: errorMessage,
        from: 'cli'
      },
    }, sessionId);
  }
}

// ğŸ”§ æ–°å¢ï¼šæ¨¡æ‹Ÿäº¤äº’å¼æ¨¡å¼çš„æµäº‹ä»¶å¤„ç†
async function processWebSocketStreamEvents(
  stream: any,
  abortSignal: AbortSignal,
  config: any,
  ws: any,
  sessionId: string,
  allResponseText: string,
  logger: WebSocketDebugLogger,
  context: any
) {
  const toolCallRequests: any[] = [];
  let responseText = '';

  for await (const event of stream) {
    if (abortSignal.aborted) {
      logger.debug('Operation aborted');
      break;
    }

    logger.debug(`Event type: ${event.type}`);

    switch (event.type) {
      case 'content':
        if (event.value) {
          const textContent = event.value;
          responseText += textContent;
          allResponseText += textContent;

          // æ˜¾ç¤ºåœ¨ç»ˆç«¯
          context.ui.addItem({
            type: MessageType.INFO,
            text: textContent
          }, Date.now());

          // å‘é€æ–‡æœ¬åˆ°web
          if (textContent.trim().length > 0) {
            ws.send({
              agent: 'cli_agent',
              payload: {
                type: 'execution_stream',
                content: textContent,
                accumulated: allResponseText,
                from: 'cli'
              },
            }, sessionId);
          }
        }
        break;

      case 'tool_call_request':
        logger.debug(`Tool call requested: ${event.value.name}`);
        logger.debug(`Tool call ID: ${event.value.callId}`);
        toolCallRequests.push(event.value);
        break;

      case 'error':
        logger.error(`Stream error:`, event.value);
        context.ui.addItem({
          type: MessageType.ERROR,
          text: `âŒ [STREAM ERROR] ${event.value.error?.message || 'Unknown error'}`
        }, Date.now());

        ws.send({
          agent: 'cli_agent',
          payload: {
            type: 'execution_error',
            error: event.value.error?.message || 'Unknown error',
            from: 'cli'
          },
        }, sessionId);
        break;

      case 'finished':
        logger.debug('Stream finished');
        break;

      default:
        logger.debug(`Unhandled event type: ${event.type}`);
    }
  }

  return {
    toolCallRequests,
    responseText,
    allResponseText
  };
}

// ğŸ”§ æ–°å¢ï¼šæ¨¡æ‹Ÿäº¤äº’å¼æ¨¡å¼çš„å·¥å…·è°ƒç”¨å¤„ç†
async function handleWebSocketToolCalls(
  toolCallRequests: any[],
  geminiClient: any,
  abortSignal: AbortSignal,
  prompt_id: string,
  config: any,
  ws: any,
  sessionId: string,
  allResponseText: string,
  logger: WebSocketDebugLogger,
  context: any
) {
  logger.debug(`Processing ${toolCallRequests.length} tool calls...`);

  const toolRegistry = await config.getToolRegistry();
  const toolResponses: any[] = [];

  for (const toolRequest of toolCallRequests) {
    // é€šçŸ¥å‰ç«¯å·¥å…·è°ƒç”¨å¼€å§‹
    context.ui.addItem({
      type: MessageType.INFO,
      text: `ğŸ”§ [TOOL START] ${toolRequest.name}(${JSON.stringify(toolRequest.args)})`
    }, Date.now());

    ws.send({
      agent: 'cli_agent',
      payload: {
        type: 'tool_call_start',
        tool: toolRequest.name,
        args: toolRequest.args,
        call_id: toolRequest.callId,
        from: 'cli'
      },
    }, sessionId);

    try {
      // æ‰§è¡Œå·¥å…·è°ƒç”¨
      const { executeToolCall } = await import('@gair/sii-cli-core');
      const toolResponse = await executeToolCall(
        config,
        toolRequest,
        toolRegistry,
        abortSignal,
      );

      logger.debug(`Tool execution completed: ${toolRequest.name}`);

      context.ui.addItem({
        type: MessageType.INFO,
        text: `âœ… [TOOL COMPLETE] ${toolRequest.name} - ${toolResponse.resultDisplay || 'Success'}`
      }, Date.now());

      // é€šçŸ¥å‰ç«¯å·¥å…·è°ƒç”¨å®Œæˆ
      ws.send({
        agent: 'cli_agent',
        payload: {
          type: 'tool_call_complete',
          tool: toolRequest.name,
          call_id: toolRequest.callId,
          result: toolResponse.resultDisplay || 'Success',
          from: 'cli',
        },
      }, sessionId);

      // ğŸ”§ å…³é”®ï¼šæ”¶é›†å·¥å…·å“åº”ç”¨äºç»§ç»­å¯¹è¯ï¼ˆä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒï¼‰
      if (toolResponse.responseParts) {
        toolResponses.push(toolResponse.responseParts);
      }

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('Tool execution failed:', error);

      context.ui.addItem({
        type: MessageType.ERROR,
        text: `âŒ [TOOL ERROR] ${toolRequest.name}: ${errorMessage}`
      }, Date.now());

      ws.send({
        agent: 'cli_agent',
        payload: {
          type: 'tool_call_error',
          tool: toolRequest.name,
          call_id: toolRequest.callId,
          error: errorMessage,
          from: 'cli',
        },
      }, sessionId);
    }
  }

  // ğŸ”§ å…³é”®ï¼šå·¥å…·æ‰§è¡Œåç»§ç»­å¯¹è¯ï¼ˆä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒï¼‰
  if (toolResponses.length > 0) {
    logger.debug(`Continuing conversation with ${toolResponses.length} tool responses...`);

    // é€’å½’è°ƒç”¨ï¼Œä¼ é€’å·¥å…·å“åº”
    await submitWebSocketQuery(
      geminiClient,
      toolResponses.flat(),
      abortSignal,
      prompt_id,
      config,
      ws,
      sessionId,
      allResponseText,
      true,
      logger,
      context
    );
  }
}

// ğŸ”§ æ–°å¢ï¼šæ¨¡æ‹Ÿäº¤äº’å¼æ¨¡å¼çš„ Next Speaker Check
async function checkWebSocketNextSpeaker(
  geminiClient: any,
  abortSignal: AbortSignal,
  config: any,
  logger: WebSocketDebugLogger
): Promise<boolean> {
  try {
    // ğŸ”§ å…³é”®ï¼šä½¿ç”¨ä¸äº¤äº’å¼æ¨¡å¼ç›¸åŒçš„ checkNextSpeaker é€»è¾‘
    const { checkNextSpeaker } = await import('@gair/sii-cli-core');

    const chat = await geminiClient.getChat();
    const nextSpeakerCheck = await checkNextSpeaker(
      chat,
      geminiClient,
      abortSignal,
      config,
    );

    return nextSpeakerCheck?.next_speaker === 'model';
  } catch (error) {
    logger.error('Next speaker check failed:', error);
    // å‡ºé”™æ—¶é»˜è®¤ç»“æŸå¯¹è¯
    return false;
  }
}

