private async checkSIIAuthentication(): Promise<{
  success: boolean;
  username?: string;
  balance?: number;
  error?: string;
}> {
  try {
    const siiBaseUrl = process.env.SII_BASE_URL || 'https://www.opensii.ai/backend';
    const username = process.env.SII_USERNAME!;
    const password = process.env.SII_PASSWORD!;

    // 1. ç™»å½•è·å– token
    const loginResponse = await fetch(`${siiBaseUrl}/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`,
    });

    if (!loginResponse.ok) {
      return { success: false, error: `SII login failed: ${loginResponse.status}` };
    }

    const loginData = await loginResponse.json();
    if (!loginData.access_token) {
      return { success: false, error: 'Invalid SII credentials' };
    }

    // 2. æ£€æŸ¥ token ä¿¡æ¯å’Œä½™é¢
    const tokenInfoResponse = await fetch(`${siiBaseUrl}/auth/user/token-info`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${loginData.access_token}` },
    });

    if (tokenInfoResponse.ok) {
      const tokenInfo = await tokenInfoResponse.json();
      if (tokenInfo.status === 'success' && tokenInfo.data) {
        const totalAvailable = tokenInfo.data.total_available || 0;
        return {
          success: totalAvailable > 0,
          username,
          balance: totalAvailable,
          error: totalAvailable === 0 ? 'Insufficient SII balance' : undefined,
        };
      }
    }

    return { success: false, error: 'Token info check failed' };
  } catch (error) {
    return { success: false, error: error instanceof Error ? error.message : 'SII connection error' };
  }
}
```

