        this.vx = (Math.random() - 0.5) * (large ? 8 : 6);
        this.vy = (Math.random() - 0.5) * (large ? 8 : 6);
        this.size = large ? Math.random() * 4 + 2 : (small ? Math.random() * 2 + 1 : Math.random() * 3 + 1);
